#define BLANK 0
#define COPPER 1
#define HEAD 2
#define TAIL 3

bool point(in ivec2 cell, in int x, in int y) {
    return cell.x == x && cell.y == y;
}

bool wire(in ivec2 cell, in int y, in int x0, in int x1) {
    return cell.y == y && cell.x >= x0 && cell.x <= x1;
}

bool clock(in ivec2 cell, in int y, in int x0, in int x1) {
    bool etch = cell.y == y && (cell.x == x0 || cell.x == x1);
    etch = etch || (abs(cell.y - y) == 1 && cell.x >= x0 + 1 && cell.x <= x1 - 1);
    return etch;
}

bool diode(in ivec2 cell, in int x, in int y, in int dir) {
    cell.x *= dir, x *= dir;
    bool etch = abs(cell.y - y) == 1 && cell.x >= x - 1 && cell.x <= x;
    etch = etch || (cell.y == y && cell.x == x - 1);
    return etch;
}

bool and(in ivec2 cell, in int x, in int y, in int dir) {
    cell.x *= dir, x *= dir;
    cell -= ivec2(5, -3);
    bool etch = cell.y == y + 3 && cell.x >= x - 4 && cell.x <= x + 2;
    etch = etch || (abs(cell.x - x + 3) == 1 && cell.y >= y - 3 && cell.y <= y - 1);
    etch = etch || (cell.x == x - 3 && cell.y == y - 4);
    etch = etch || (cell.x == x && abs(cell.y - y) < 2);
    etch = etch || (cell.y == y && abs(cell.x - x) == 1);
    etch = etch || (cell.x == x + 2 && abs(cell.y - y) == 1);
    etch = etch || (cell.x == x + 4 && abs(cell.y - y + 2) < 2);
    etch = etch || (cell.y == y - 2 && abs(cell.x - x - 4) == 1);
    etch = etch || (cell.y == y + 2 && cell.x >= x + 3 && cell.x <= x + 5);
    etch = etch || (cell.x == x + 6 && cell.y >= y - 1 && cell.y <= y + 1);
    etch = etch || (cell.y == y - 3 && cell.x >= x + 6 && cell.x <= x + 7);
    etch = etch || (cell.x == x + 8 && cell.y == y - 2);
    etch = etch || (cell.y == y - 1 && cell.x >= x + 9 && cell.x <= x + 10);
    return etch;
}

bool or(in ivec2 cell, in int x, in int y, in int dir) {
    cell.x *= dir, x *= dir;
    cell -= ivec2(1, -2);
    bool etch = cell.x == x && abs(cell.y - y) < 2;
    etch = etch || (abs(cell.x - x) < 2 && cell.y == y);
    return etch;
}

bool xor(in ivec2 cell, in int x, in int y, in int dir) {
    cell.x *= dir, x *= dir;
    cell -= ivec2(1, -3);
    bool etch = cell.x == x - 1 && abs(cell.y - y) < 2;
    etch = etch || (abs(cell.y - y) == 1 && cell.x >= x - 1 && cell.x <= x + 2);
    etch = etch || (cell.y == y && cell.x >= x + 2 && cell.x <= x + 3);
    etch = etch || (cell.x == x && abs(cell.y - y) == 2);
    return etch;
}

bool flipflop(in ivec2 cell, in int x, in int y, in int dir) {
    cell.x *= dir, x *= dir;
    bool etch = cell.x == x + 1 && cell.y == y;
    etch = etch || (cell.x == x + 2 && abs(cell.y - y + 2) < 2);
    etch = etch || (abs(cell.x - x - 2) == 1 && cell.y == y - 2);
    etch = etch || (cell.x == x + 1 && cell.y >= y - 7 && cell.y <= y - 4);
    etch = etch || (abs(cell.x - x - 1) == 1 && cell.y == y - 6);
    etch = etch || (cell.x == x + 3 && cell.y >= y - 5 && cell.y <= y - 4);
    return etch;
}

#define Etch(copper) etch = etch || (copper)
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(BLANK);
    ivec2 cell = ivec2(fragCoord - 0.5);
    if (iFrame == 0) {
        // Fast clock (1/6)cycles/frame
        bool etch = clock(cell, 2, 1, 4);
        Etch(wire(cell, 2, 5, 11));
        Etch(wire(cell, 6 - 3, 12, 16));
        Etch(wire(cell.yx, 17, 4, 19));
        Etch(wire(cell, 20, 18, 20));

        // 3x slower than first clock
        Etch(clock(cell, 6, 1, 10));
        Etch(wire(cell, 6, 11, 14));
        Etch(wire(cell.yx, 15, 7, 22));
        Etch(wire(cell, 23, 16, 20));

        Etch(and(cell, 20, 23, 1));

        // Slooow clock (1/124)cycles/frame
        Etch(wire(cell, 13, 20, 33));
        Etch(point(cell, 19, 12));
        Etch(wire(cell, 11, 20, 31));
        Etch(point(cell, 32, 10));
        Etch(point(cell, 19, 8));
        Etch(wire(cell, 9, 20, 31));
        Etch(wire(cell, 7, 20, 40));
        Etch(point(cell, 41, 6));
        Etch(wire(cell, 5, 20, 40));
        Etch(point(cell, 19, 4));
        Etch(wire(cell, 3, 20, 42));
        Etch(wire(cell.yx, 43, 4, 8));
        Etch(wire(cell, 9, 35, 42));
        Etch(wire(cell.yx, 34, 10, 12));

        // Flipflop
        Etch(flipflop(cell, 35, 19, 1));
        Etch(wire(cell, 15, 39, 45));

        // Display
        Etch(wire(cell.yx, 46, 16, 36));
        Etch(point(cell, 47, 37));
        Etch(wire(cell.yx, 48, 16, 36));
        Etch(point(cell, 49, 15));
        Etch(wire(cell.yx, 50, 16, 36));
        Etch(point(cell, 51, 37));
        Etch(wire(cell.yx, 52, 16, 36));
        Etch(point(cell, 53, 15));
        Etch(wire(cell.yx, 54, 16, 36));

        if (etch) fragColor = vec4(COPPER);

        if (point(cell, 4, 2)) fragColor = vec4(HEAD);
        if (point(cell, 3, 1)) fragColor = vec4(TAIL);

        if (point(cell, 10, 6)) fragColor = vec4(HEAD);
        if (point(cell, 9, 5)) fragColor = vec4(TAIL);

        if (point(cell, 34, 12)) fragColor = vec4(HEAD);
        if (point(cell, 33, 13)) fragColor = vec4(TAIL);
    }

    else {
        fragColor = texelFetch(iChannel0, cell, 0);
        if (iFrame % 5 == 0) {
            int state = int(texelFetch(iChannel0, cell, 0).x);

            int heads = 0;
            for (int i=-1; i <= 1; i++) {
                for (int j=-1; j <= 1; j++) {
                    if (!(i == 0 && j == 0)) {
                        int neighbor = int(texelFetch(iChannel0, cell + ivec2(i, j), 0).x);
                        if (neighbor == HEAD) heads++;
                    }
                }
            }

            if (state == COPPER && (heads == 1 || heads == 2)) state = HEAD;
            else if (state == HEAD) state = TAIL;
            else if (state == TAIL) state = COPPER;

            fragColor = vec4(state);
        }
    }
}

