    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - vec2(1.0);
    uv.x /= iResolution.y / iResolution.x;
    vec3 camera_pos = (vec3(sin(iTime), cos(iTime), -3.0) * 0.1) + vec3(0.5, 0.5, 0.0);
    vec3 ray_pos = camera_pos;

    vec3 ray_step = normalize(vec3(uv * screenSize, screenDist)) * rayLength;

    vec4 color = vec4(0.0);

    for (int i = 0; i < ITER; i++) {
        ray_pos += ray_step;
        vec4 voxel = texture(iChannel0, ray_pos);

        // (optional) Making dense voxels more clumped 
        voxel += texture(iChannel0, ray_pos / 2.0);
        voxel += texture(iChannel0, ray_pos / 4.0);
        voxel += texture(iChannel0, ray_pos / 8.0);
        voxel *= 0.25;

        // (optional) Making density more different
        voxel.a = smoothstep(0.0, 1.0, pow(voxel.a, 10.0));

        // Compute absolute density
        voxel.a = mix(voxel.a, 1.0, color.a) - color.a; 

        // Applying color
        color.rgb += voxel.rgb * voxel.a;
        color.a += voxel.a;
    }

    fragColor = color;

}

// CC0: F# Windows Terminal Shader
//  A shader background for Windows Terminal featuring the F# logo

#define TIME        iTime
#define RESOLUTION  iResolution


#define PI          3.141592654
#define TAU         (2.0*PI)

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float hexagon(vec2 p, float r) {
//  const vec3 k = vec3(-0.866025404,0.5,0.577350269);
  const vec3 k = 0.5*vec3(-sqrt(3.0),1.0,sqrt(4.0/3.0));
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

// License: Unknown, author: Martijn Steinrucken, found: https://www.youtube.com/watch?v=VmrIDyYiJBA
vec2 hextile(inout vec2 p) {
  // See Art of Code: Hexagonal Tiling Explained!
  // https://www.youtube.com/watch?v=VmrIDyYiJBA
  const vec2 sz       = vec2(1.0, sqrt(3.0));
  const vec2 hsz      = 0.5*sz;

  vec2 p1 = mod(p, sz)-hsz;
  vec2 p2 = mod(p - hsz, sz)-hsz;
  vec2 p3 = dot(p1, p1) < dot(p2, p2) ? p1 : p2;
  vec2 n = ((p3 - p + hsz)/sz);
  p = p3;

  n -= vec2(0.5);
  // Rounding to make hextile 0,0 well behaved
  return round(n*2.0)*0.5;
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

vec2 pmin(vec2 a, vec2 b, float k) {
  vec2 h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

vec2 pabs(vec2 a, float k) {
  return -pmin(-a, a, k);
}

vec2 dfsharp(vec2 p) {
  vec2 p0 = p;
  vec2 p1 = p;
  vec2 p3 = p;
  const float sm = 0.03;
  p0 = pabs(p0, sm);
  const vec2 n = normalize(vec2(1.0));
  float d0 = abs(dot(n, p0)-0.38)-0.12;
  float d1 = abs(p1.x)-0.025;
  float d2 = dot(n, p0)-0.19;
  float d3 = -p3.x-0.025;
  d2 = pmax(d2, -d3, sm);
  float d = d0;

  d = pmax(d, -d1, sm);
  d = min(d,  d2);
  return vec2(d, p.x > 0.0 ? 1.0 : 0.0);
}

float cellf(vec2 p, vec2 n) {
  const float lw = 0.01;
  return -hexagon(p.yx, 0.5-lw);
}

vec2 df(vec2 p, out vec2 hn0, out vec2 hn1) {
  const float sz = 0.25;
  p /= sz;
  vec2 hp0 = p;
  vec2 hp1 = p+vec2(1.0, sqrt(1.0/3.0));

  hn0 = hextile(hp0);
  hn1 = hextile(hp1);

  float d0 = cellf(hp0, hn0);
  float d1 = cellf(hp1, hn1);
  float d2 = length(hp0);

  float d = d0;
  d = min(d0, d1);

  return vec2(d, d2)*sz;
}

vec3 effect(vec2 p, vec2 pp) {
  const float pa = 20.0;
  const float pf = 0.0025;
  const float hoff = 0.0;
  const vec3 fcol0 = HSV2RGB(vec3(hoff+0.62, 0.95, 1.0));
  const vec3 fcol1 = HSV2RGB(vec3(hoff+0.62, 0.75, 1.0));
  const vec3 bcol0 = HSV2RGB(vec3(hoff+0.63, 0.85, 0.5));

  float aa = 2.0/RESOLUTION.y;
  vec2 hn0;
  vec2 hn1;
  vec2 df2 = dfsharp(p);
  vec2 dfs2 = dfsharp(p-vec2(0.01, -0.01));
  vec2 pb = p + pa*sin(TIME*pf*vec2(1.0, sqrt(0.5)));
  vec2 d2 = df(pb, hn0, hn1);

  vec3 col = vec3(0.0);

  float h0 = hash(hn1);
  float l = mix(0.25, 0.75, h0);

  if (hn0.x <= hn1.x+0.5) {
    l *= 0.5;
  }

  if (hn0.y <= hn1.y) {
    l *= 0.75;
  }

  col += l*bcol0;

  col = mix(col, vec3(0.), smoothstep(aa, -aa, d2.x));
  col *= mix(0.75, 1.0, smoothstep(0.01, 0.2, d2.y));
  col = mix(col, col*0.2, smoothstep(aa, -aa, 0.125*dfs2.x));
  col = mix(col, smoothstep(-1.5, 0.5, p.y)*mix(fcol0, fcol1, df2.y > 0.0 ? 1.0 : 0.0), smoothstep(aa, -aa, df2.x)); 
  col *= 1.25*smoothstep(1.5, 0.25, length(pp));
//  col *= mix(vec3(0.5), vec3(1.0),smoothstep(-0.9, 0.9, sin(0.25*TAU*p.y/aa+TAU*vec3(0.0, 1., 2.0)/3.0)));
  return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp);
  col = sqrt(col);
  fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 250
#define MAX_DIST 100.
#define SURF_DIST .01
#define SSK 8.
#define PI 3.14159265
#define FOV 90.
#define OFFSET SURF_DIST*1.05
#define bounces 1
#define GI vec3(1.,.9,.75)*.1
#define LP vec3(1.25*sin(iTime*2./PI),22.5,1.25*cos(iTime*2./PI));
#define LC vec4(1.,.95,.85,fract(356.*sin(floor(iTime*230.)/230.))/.99 > 1. ? .4 : 2.)

//////
vec4 texture2(sampler2D s,vec2 p) {
    return pow(texture(s,p),vec4(2.2));
}

vec3 skybox(vec3 rd) {
    vec3 skybox = pow(texture(iChannel0,rd).xyz,vec3(2.2));
    //vec3 skybox = vec3(.5,.75,.95);  //blue sky
    return skybox;
}

vec2 rot(vec2 p,float a) {
    return vec2(p.x*cos(a)+p.y*sin(a),p.x*sin(a)-p.y*cos(a));
}

/////SDFs

float sdSphere(vec3 p, vec4 sphere) {
    return length(p-sphere.xyz)-sphere.w;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdBox(vec3 p, vec3 a, vec4 b) {
    vec3 q = abs(p-a)-b.xyz;
    return length(max(q,0.))+min(max(q.x,max(q.y,q.z)),0.)-b.w;
}


/////World
float map(in vec3 p, bool il){
    vec3 lp = LP;
    float d0 = sdBox(p,vec3(0.,0.,0.),vec4(10.5,0.,10.5,.5));
    float d1 = sdBox(p,vec3(0.,-1.,0.),vec4(25.,1.,25.,0.));
    float d2 = sdBox(p,vec3(0.,10.,0.),vec4(25.,20.,25.,.1));
    float d3 = sdSphere(p,vec4(25.,11.,3.,5.));
    float d4 = sdBox(p,vec3(6.,8.5,25.),vec4(4.,8.,.4,0.));
    float d5 = max(sdBox(p,vec3(6.,8.5,25.),vec4(4.5,8.5,.5,0.)),-sdBox(p,vec3(6.,8.5,25.),vec4(4.,8.,.55,0.)));
    float d6 = il ? sdSphere(p,vec4(lp,.5)) : MAX_DIST;
    vec3 cp = lp+normalize(vec3(0.,30.,0.)-lp)*.1;
    float d7 = il ? sdCapsule(p,vec3(0.,30.,0.),cp,.1) : MAX_DIST;
    float d = min(d7,min(d6,min(min(min(d0,min(d1,max(abs(d2)-.4,-d3))),d4),d5)));
    return d;
}

vec3 getNormal(vec3 p) {
    float d0 = map(p,true);
    vec2 e = vec2(.01,0.);
    vec3 n = d0-vec3(
    map(p-e.xyy,true),
    map(p-e.yxy,true),
    map(p-e.yyx,true));

    return normalize(n);
}

vec3 trimap(vec3 p, sampler2D s, float k, float rk) {
    vec3 n = pow(getNormal(p),vec3(k));
    vec3 tx = texture2(s, p.yz/rk).xyz;
    vec3 ty = texture2(s, p.xz/rk).xyz;
    vec3 tz = texture2(s, p.xy/rk).xyz;
    return (tx*n.x + ty*n.y+tz*n.z)/(n.x+n.y+n.z);
}


void getMat(in vec3 p, out vec3 objc, out vec3 objld, out vec2 objrd, out bool frneliz) {
    vec3 lp = LP;
    float d0 = sdBox(p,vec3(0.,0.,0.),vec4(10.5,0.,10.5,.5));
    float d1 = sdBox(p,vec3(0.,-1.,0.),vec4(25.,1.,25.,0.));
    float d2 = sdBox(p,vec3(0.,10.,0.),vec4(25.,20.,25.,.1));
    float d3 = sdSphere(p,vec4(25.,11.,3.,5.));
    float d4 = sdBox(p,vec3(6.,8.5,25.),vec4(4.,8.,.4,0.));
    float d5 = max(sdBox(p,vec3(6.,8.5,25.),vec4(4.5,8.5,.5,0.)),-sdBox(p,vec3(6.,8.5,25.),vec4(4.,8.,.55,0.)));
    float d6 = sdSphere(p,vec4(lp,.5));
    vec3 cp = lp+normalize(vec3(0.,30.,0.)-lp)*.1;
    float d7 = sdCapsule(p,vec3(0.,30.,0.),cp,.1);
    float d = min(d7,min(d6,min(min(min(d0,min(d1,max(abs(d2)-.4,-d3))),d4),d5)));

    if(d==d1){
        objc = vec3(.8,.95,1.)*texture2(iChannel2,p.xz/14.).xyz;
        objld = vec3(1.,.3,60.);
        objrd = vec2(.1,.1);
        frneliz = true;
    } else if(d==d0) {
        objc = vec3(.8,.3,.25)*texture2(iChannel1,p.xz/14.).xyz;
        objld = vec3(1.,.0,0.);
        objrd = vec2(.0,1.);
        frneliz = false;
    } else if(d==max(abs(d2)-.4,-d3)) {
        objc = vec3(.8,.95,1.)*trimap(p,iChannel2,10.,14.);
        objld = vec3(1.,.3,60.);
        objrd = vec2(.1,.1);
        frneliz = true;
    } else if(d==d4) {
        objc = ((1.-vec3(1.,.9,.75))+vec3(.75))/1.75;
        objld = vec3(.7,.2,60.);
        objrd = vec2(.7,1.);
        frneliz = false;
    } else if(d==d5) {
        objc = vec3(.9,.8,.6);
        objld = vec3(1.,.1,120.);
        objrd = vec2(.1,.3);
        frneliz = true;
    } else if(d==d6) {
        objc = vec3(LC.xyz*LC.w/1.5);
        objld = vec3(1.,1.,5.);
        objrd = vec2(.1,1.);
        frneliz = false;
    } else if(d==d7) {
        objc = vec3(0.,0.,0.);
        objld = vec3(0.,0.,0.);
        objrd = vec2(.0,1.);
        frneliz = false;
    }

}


float raymarch(in vec3 rd, in vec3 ro, out bool hit,float maxd) {  
    float h = 0.;                                                         
    hit = false;
    for(int i = 0; i < MAX_STEPS; i++){
        vec3 p = ro + rd*h;
        float d = map(p,true);
        if (d < SURF_DIST) {
            hit = true;
            break;
        }
        if (h > maxd) break;
        h += d;
    }
    return h;
}

float shadow(vec3 p, vec3 rd, float len) {
    float ss = 1.;
    float h = 0.;
    for(int t = 0; t < MAX_STEPS && h < len; t++) {
       vec3 cp = p+rd*h;
       float d = map(cp,false);
       if(d < SURF_DIST) return 0.;
       h += d;
       ss = min(ss,SSK*d/h);
    }
    return ss;
}

vec3 diffuse(vec3 p,vec3 lp0, vec4 lc, vec3 objc) {
    vec3 n = getNormal(p);
    vec3 l = normalize(lp0);

    float ss = shadow(p+n*OFFSET,l,length(lp0));
    float dif = dot(l,n);
    return dif*lc.xyz*ss*objc*lc.w;
}

vec3 specular(vec3 p,vec3 ev, vec3 lv, vec4 lc, float ld) {
    vec3 n = getNormal(p);
    vec3 r = reflect(ev,n);
    float spec = pow(max(dot(lv,r),0.),ld);
    return lc.xyz*spec*lc.w;
}


//////Drawing
void castAndCol(inout vec3 rd, inout vec3 ro, inout vec3 col, float refPow, out float refPowMult) {
    vec3 lp1 = LP;
    vec4 lc1 = LC;

    bool hit = false;

    float d = raymarch(rd,ro,hit,MAX_DIST);

    if (hit == true) {
        vec3 objc = vec3(.8,.85,1.);
        vec3 objld = vec3(1.,1.,10.);
        vec2 objrd = vec2(.5);
        bool frneliz = true;

        vec3 p = ro+rd*d;
        vec3 n = getNormal(p);
        vec3 r = reflect(rd,n);
        getMat(p,objc,objld,objrd,frneliz);

        float refAmt = 1.;
        if(frneliz == true) {
            refAmt = clamp(pow(1.-dot(-rd,n),8.),0.,1.)/objrd.y;
        }

        vec3 dif = diffuse(p,lp1-p,lc1,objc);
        vec3 bdif = diffuse(p,p-.1*normalize(lp1-p),lc1,objc);
        vec3 spec = specular(p,rd,normalize(lp1-p),lc1,objld.z);
        col += dif*objld.x*refPow;
        col += bdif*objld.x*refPow/2.;
        col += spec*objld.y*refPow;

        refPowMult = refAmt*objrd.x;

        ro = p+n*OFFSET;
        rd = r;
    } else {
        vec3 sky = skybox(rd);
        col += sky*refPow;
        refPowMult = 0.;

    }
}

vec3 crd(vec3 ro, vec2 fragCoord) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = PI*((vec2(iMouse.x*2.,iMouse.y)/iResolution.xy)-.5);
    if(iMouse.xy == vec2(0.)) mouse.xy = vec2(PI/8.,-PI*.025);

    vec3 rd = normalize(vec3(uv.xy,1./tan(FOV*PI/360.)));

    vec2 rotAmt = vec2(atan(ro.x,-ro.z),mouse.y-PI);
    rd = vec3(rd.x,rot(rd.yz,rotAmt.y));
    rd.y = -rd.y;
    vec2 rotRd = rot(rd.zx,rotAmt.x);
    rd = vec3(-rotRd.y,rd.y,rotRd.x);
    return rd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 mouse = PI*((vec2(iMouse.x*2.,iMouse.y)/iResolution.xy)-.5);
    vec3 ro = vec3(20.*sin(-mouse.x),10.,-20.*cos(-mouse.x));
    vec3 rd = crd(ro,fragCoord);
    vec3 rdOrig = rd;

    vec3 col = vec3(0.);
    float refPow = 1.;
    float refPowMult = 1.;
    castAndCol(rd,ro,col,refPow,refPowMult);

    for(int i = 0; i < bounces; i++) {
        refPow *= refPowMult;
        castAndCol(rd,ro,col,refPow,refPowMult);
    }

    col += GI;

    fragColor = vec4(clamp(pow(col,vec3(1./2.2)),0.,1.),1.0);
}

/*
Simple distortion effects, ported from Unity's shader graph documentation:
https://docs.unity3d.com/Packages/com.unity.shadergraph@6.9/manual/UV-Nodes.html

Code for effects is found in the common tab!

Effect order:

    Source image    Radial shear 

       Twirl          Spherize   


The mouse controls the effect centering
*/



// Uncomment to see how the effects distort uv-space
//#define SHOW_UV

// Controls the effect strengths
vec2 radial_strength = vec2(5.0);
vec2 spherize_strength = vec2(15.0);
float twirl_strength = 10.0;

// Set to 0 to turn off animations
float ANIMATION = 1.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get 2x2 tiled uvs, centered on screen
    // -> uv_n1p1 is 'uvs going from negative 1 to positive 1'
    // -> uv_01_tile is 'uvs going from 0 to 1, tiled'
    vec2 cen_coord = fragCoord - vec2(iResolution.x * 0.5, 0);
    vec2 uv_n1p1 = 2.0 * (cen_coord / iResolution.y) - vec2(0.0, 1.0);
    vec2 uv_01_tile = fract(uv_n1p1);

    // For helping to debug the uv mess
    //fragColor = vec4(uv_n1p1,0,1); return;
    //fragColor = vec4(uv_01_tile, 0, 1); return;

    // Knock out side bars
    bool is_out_of_bounds = abs(uv_n1p1.x) > 1.0;
    if(is_out_of_bounds) {
        fragColor = vec4(0);
        return;
    }

    // Control the effect centering with the mouse
    float is_mouse_down = clamp(iMouse.z, 0., 1.);
    vec2 mouse_center_px = iMouse.xy - vec2(iResolution.x * 0.5, 0);
    vec2 mouse_center_01 = fract(2.0 * (mouse_center_px / iResolution.y) - vec2(0.0, 1.0));
    vec2 center = mix(vec2(0.5), mouse_center_01, is_mouse_down);

    // For clarity
    bool is_top_left = (uv_n1p1.x < 0.0) && (uv_n1p1.y > 0.0);
    bool is_top_right = (uv_n1p1.x > 0.0) && (uv_n1p1.y > 0.0);
    bool is_bot_left = (uv_n1p1.x < 0.0) && (uv_n1p1.y < 0.0);
    bool is_bot_right = (uv_n1p1.x > 0.0) && (uv_n1p1.y < 0.0);
    vec2 warp_uv;

    // No warping    
    if (is_top_left) {
        warp_uv = uv_01_tile;
    }

    // Radial shear (the flowing effect)
    if (is_top_right) {
        warp_uv = radial_shear(uv_01_tile, center, radial_strength);
        warp_uv += vec2(iTime * 0.5, 0.0) * ANIMATION;

        // For nicer uv debug coloring, not needed for the effect (needed for videos!)
        // -> Introduces a slight glitch at uv boundaries
        warp_uv = fract(warp_uv);
    }

    // Twirl
    if (is_bot_left) {
        twirl_strength *= cos(iTime * 0.25 * ANIMATION);
        warp_uv = twirl(uv_01_tile, center, twirl_strength);
    }

    // Spherize
    if (is_bot_right) {
        spherize_strength *= cos(iTime * 0.5 * ANIMATION);
        warp_uv = spherize(uv_01_tile, center, spherize_strength);
    }

    // Look up pixel values using distorted uvs
    fragColor = texture(iChannel0, warp_uv);

    // Show uv coloring for debug
    #ifdef SHOW_UV
        fragColor = vec4(warp_uv, 0, 1);
    #endif
}

common_code:
vec2 radial_shear(vec2 uv, vec2 center, vec2 strength)
{
    /*
    Source:
    https://docs.unity3d.com/Packages/com.unity.shadergraph@6.9/manual/Radial-Shear-Node.html
    */
    vec2 uv_cen = uv - center;
    vec2 scaled_dist_sq = strength * dot(uv_cen, uv_cen);
    return uv + vec2(uv_cen.y, -uv_cen.x) * scaled_dist_sq;
}

vec2 twirl(vec2 uv, vec2 center, float strength)
{
    /*
    Source:
    https://docs.unity3d.com/Packages/com.unity.shadergraph@6.9/manual/Twirl-Node.html
    */

    vec2 uv_cen = uv - center;
    float scaled_dist = strength * length(uv_cen);
    vec2 cs = vec2(cos(scaled_dist), sin(scaled_dist));

    float x_twirl = dot(cs * vec2(1.0, -1.0), uv_cen);
    float y_twirl = dot(cs.yx, uv_cen);

    return vec2(x_twirl + center.x, y_twirl + center.y);
}

vec2 spherize(vec2 uv, vec2 center, vec2 strength)
{
    /*
    Source:
    https://docs.unity3d.com/Packages/com.unity.shadergraph@6.9/manual/Spherize-Node.html
    */
    vec2 uv_cen = uv - center;
    float dist_sq = dot(uv_cen, uv_cen);
    float dist_to_4th = dist_sq * dist_sq;
    vec2 sphere_offset = strength * dist_to_4th;
    return uv + uv_cen * sphere_offset;
}

