vec3 scene(Ray r, in vec2 fragCoord, inout uint rngState)
{
    Sphere background = Sphere(100000., vec3(0));
    Sphere s = Sphere(0.5, vec3(0));

    Hit hit = intersectSphere(background, r);
    swapIfCloser(hit, intersectSphere(s, r));

    vec3 color = (hit.normal + vec3(1)) * 10.0;
    color -= floor(color);
    return (color + hit.normal * 0.5 + 0.5) * 0.5;
}

Ray camTF(Ray r, in vec2 fragCoord, inout uint rngState)
{
    r.origin = vec3(0, 0, -1.5);

    quaternion q = EulerZXY(vec3
    (
        (iMouse.yx / iResolution.yx * 2.0 - 1.0) * vec2(-Pi, Pi), 
        0
    ));

    r.origin = mul(q, r.origin);
    r.direction = mul(q, r.direction);

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint rngState = uint
    (
        uint(iFrame) * uint(1973) 
    ) | uint(1);

    // Using time-varying blue noise as subpixel offsets 
    // will kill persistent moire patterns.
    // If you modify the shader to accumulate,
    // it'll converge on a nice image with no aliasing.
    // (It uses gaussian windowing, not an ideal lowpass filter however.)
    vec4 blueNoise = sampleBlueNoise(iChannel3, fragCoord, rngState);
    vec4 gaussCoords = uniform01ToGauss(blueNoise.xy).xyxy * (vec4(1, 1, -1, -1) * 1.0);
    const int msaaCount = 4;
    vec2[msaaCount] msaaCoords;
    msaaCoords[0] = gaussCoords.xy;
    msaaCoords[1] = gaussCoords.wx;
    msaaCoords[2] = gaussCoords.zw;
    msaaCoords[3] = gaussCoords.yz;

	vec3 color = vec3(0);
    render
    (
        color, 

        // Scene to render
        scene, 

        // Camera transform function
        camTF,

        fragCoord, 
        iResolution.xy, 

        // Zoom
        2.0,

        // MSAA coords and count
        msaaCoords, 
        msaaCount,

        rngState
    );

	fragColor = vec4(color,1.0);
}

