#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 realuv = (fragCoord/iResolution.xy - .5)*iResolution.xy/iResolution.xx*2.;

    float a = .1;
    float sq3 = sqrt(3.);
    float aM = 1./cos(PI/3.);
    vec3 col = vec3(1.);
    vec2 uv;
    mat2 rot;
    float tim = iTime * .1;
    float sinus, cosinus,angle;
    float count = (sin(iTime*.3)*.5+.53)*50.;
    for(float i=0.; i<1.; i+=1./count){
        angle=sin(i*.4*tim+tim)*PI;
        sinus=sin(angle);
        cosinus=cos(angle);
        rot=mat2(cosinus, -sinus, sinus, cosinus);
        uv=realuv*rot;
        a=i;
        col = vec3(
            min(col.x, 
                (i+
                    max(
                        max(
                            smoothstep(a,a+1./iResolution.y, abs(uv.y)),
                            smoothstep(a*aM,a*aM+aM/iResolution.y, abs(uv.y+uv.x*sq3))
                        ),
                        smoothstep(a*aM,a*aM+aM/iResolution.y, abs(uv.y-uv.x*sq3))
                    )
                )
            )        
        );
    }

    col = vec3(col.x*(sin(iTime)*.5+1.)*2.+iTime);

    vec3 blurred = vec3(0.);
    float bs =25.;
    for(float x=-bs; x<=bs; x+=bs){
        for(float y=-bs; y<=bs; y+=bs){
            blurred+=texture(iChannel0, (fragCoord+vec2(x,y))/iResolution.xy).xyz;
        }
    }
    blurred/=9.;
    float mx=.95;
    col = col*(1.+mx)-mx*blurred;

    // Output to screen
    fragColor = vec4(col,1.0);
}

