float f_n_rand(
    vec2 o
){
    return fract(sin(o.x*331.5543+o.y*173.32112)*11.23*3232.33 + o.x*o.y);
}
float f_n_dist(
    vec2 o_pix_coo_nor, 
    vec2 o_pix_coo_nor_ds,
    float n_ds
){
    float n_rand = f_n_rand(o_pix_coo_nor_ds);
    float n_t = iTime*20.;
    float n_rand2 = sin(n_rand*n_t);
    float n_rand3 = cos(n_rand*n_t);
    vec2 o_half = vec2(1./(iResolution.x/n_ds));
    vec2 o_rand = vec2(
        o_half.x*(n_rand2),
        o_half.x*(n_rand3)
        );
    float n_dist = length(o_pix_coo_nor_ds+o_half+o_rand - o_pix_coo_nor);
    return n_dist;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pix_coo_nor = ((fragCoord.xy) - (iResolution.xy)*0.5) /(iResolution.y);
    vec2 o_mou_coo_nor = ((iMouse.xy) - (iResolution.xy)*0.5) /(iResolution.y);

    float n_new_pixel_size = 1.0;//(99.*o_mou_coo_nor.x);

    float [] a_n_new_pixel_size = float[] (1.,2.,3.,4.,9.,15.,33.,66., 99.);
    for(float n_i = 0.0; n_i < float(a_n_new_pixel_size.length()); n_i+=1.){
        if(o_pix_coo_nor.x > (1./float(a_n_new_pixel_size.length()))*n_i-0.5){
            n_new_pixel_size = a_n_new_pixel_size[int(n_i)];

        }
    }

    vec2 o_fragCoord_ds = floor(fragCoord / n_new_pixel_size);
    vec3 o_iRes_ds = (iResolution / n_new_pixel_size);
    vec2 o_pix_coo_nor_ds = ((o_fragCoord_ds.xy) - (o_iRes_ds.xy * 0.5)) / o_iRes_ds.y; 

    float n_dist = f_n_dist(o_pix_coo_nor, o_pix_coo_nor_ds, n_new_pixel_size);

    vec2 o_kernel = vec2(3.);
    float n_dist_min = 1.;
    for(float n_i_kernel = 0.0; n_i_kernel<(o_kernel.x*o_kernel.y); n_i_kernel+=1.){
        float n_x_k = mod(n_i_kernel, o_kernel.x);
        float n_y_k = floor(n_i_kernel / o_kernel.x);
        n_x_k-=1.;
        n_y_k-=1.;

        vec2 o_fragCoord_ds2 = floor((fragCoord) / n_new_pixel_size) + vec2(n_x_k, n_y_k);
        vec2 o_pix_coo_nor_ds2 = ((o_fragCoord_ds2.xy) - (o_iRes_ds.xy * 0.5)) / o_iRes_ds.y; 

        float n_dist2 = f_n_dist(
            o_pix_coo_nor,
            o_pix_coo_nor_ds2,
            n_new_pixel_size
        );
        n_dist_min = min(n_dist_min, n_dist2);
        fragColor = mix(fragColor, vec4(n_dist2), 0.2);

    }
    float n_max_dist = sqrt(pow(iResolution.y/n_new_pixel_size, 2.));
    fragColor = vec4(n_dist_min*n_max_dist);
}

vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}

float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}


