////////////////////////////////////////////////////////////////////////////////
//
// Star Chart. mla, 2022.
//
// The 2000 brightest stars. Equirectangular RA/Declination projection.
//
////////////////////////////////////////////////////////////////////////////////


float bvfact = 4.0;
vec3 bvcol(float bv) {
  // Some attempt to show "bv" colour so eg. Betelgeuse comes out as pinkish.
  vec3 col = vec3(1);
  if (bv > 0.0) {
	if (bv > bvfact) bv = bvfact;
	col.gb *= (bvfact - bv)/bvfact;
  } else if (bv < 0.0) {
    if (bv < -bvfact) bv = -bvfact;
	col.rg *= (bvfact + bv)/bvfact;
  }
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.x;
  float px = PI*fwidth(length(p));
  float ra = PI*(1.0-p.x);
  float dec = PI*p.y;
  ra = mod(ra-0.1*iTime,TWOPI);
  float delta = 0.02; // Search range
  float rastart = mod(ra-delta,TWOPI); // Start of range
  int i = 0, j = nstars;
  while (i != j) {
    // Binary search
    int k = i+(j-i)/2;
    if (RA(stars[k]) < rastart) i = k+1;
    else j = k;
  }
  assert(i == 0 || RA(stars[i-1]) < rastart);
  assert(i == nstars || RA(stars[i]) >= rastart);
  vec3 col = abs(dec)<0.5*PI ? vec3(0,0,0.01) : vec3(0);
  for (int j = 0; j < 60; j++) {
    assert(j < 59);
    vec4 star = stars[(i+j)%nstars];
    float ra1 = RA(star);
    if (mod(ra1-rastart,TWOPI) > 2.0*delta) break;
    float dec1 = DEC(star);
    if (abs(dec-dec1) > delta) continue;
    float mag = 0.5*exp(-0.4*MAG(star));
    float bv = BV(star);
    vec3 starcol = vec3(1);
    starcol *= bvcol(bv);
    // Adjust brightness
    float thresh = 50.0;
    if (mag/thresh < px) starcol *= pow(mag/(thresh*px),3.0);
    float d = distance(vec2(ra,dec),vec2(ra1,dec1));
    col = mix(starcol,col,smoothstep(0.0,max(px,mag/thresh),d));
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
