    // Time varying pixel color
    float x = length(vec2(uv.x*1.73,uv.y) - vec2(0.85, 0.5+wiggle));

    // Output to screen
    if(x<0.25)
    fragColor = vec4(1.0,0,0.1,0);
    else
    fragColor = vec4(1,1,1,1);
}

float shadow(float time, vec2 uv)
{
    float velx=1.0;
    float freqx=1.0;
    float vely=1.0;
    float freqy=0.5;
    float timeshiftx = cos(iTime*velx) + (uv.x*freqx);
    float timeshifty = sin(iTime*vely) + (uv.y*freqy);
    float shadow = 0.5+(timeshiftx)*(timeshifty)*0.5;
    return shadow;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

