    // Output to screen
    if(uv.x>0.7+wiggle)
    {
      fragColor = vec4(1.0,0,0.1,0)*shadow(iTime,uv);
    }
    else
    {
      fragColor = vec4(1,1,1,1)*shadow(iTime,uv);
    }
    if(uv.x<0.3+wiggle)
    {
      fragColor = vec4(0,0,0.9,0)*shadow(iTime,uv);
    }
}

#define scale 8.

float perlin(vec2 p){
    vec2 i = floor(p); //  
    vec2 f = fract(p); //   

    float p0 = random( i );
    p0 = 0.5 + 0.5*sin( iTime + 6.2831*p0 );
    float p1 = random( i + vec2( 1.0, 0.0 ));
    p1 = 0.5 + 0.5*sin( iTime + 6.2831*p1 );
    float p2 = random( i + vec2( 0.0, 1.0 ));
    p2 = 0.5 + 0.5*sin( iTime + 6.2831*p2 );
    float p3 = random( i + vec2( 1.0, 1.0 ));
    p3 = 0.5 + 0.5*sin( iTime + 6.2831*p3 );

    vec2 u = f*f*(3.0-2.0*f); // 

    return mix(mix(p0,p1,u.x),mix(p2,p3,u.x),u.y); 
}

float worley(vec2 p){
    vec2 i = floor(p); //  
    vec2 f = fract(p); //   

    float feature = 1.0;
    for(int m=-1;m<=1;m++){
        for(int n=-1;n<=1;n++){
            vec2 offset = vec2(float(m),float(n));
            vec2 point = random22(i + offset); // 
            point = 0.5 + 0.5*sin( iTime + 6.2831*point );
            float d = length(point + offset - f);
            feature = min(feature,d);
        }
    }
    return 1.0 - feature;
}

float fbm4_p(vec2 p){
    float noiseValue = 0.0;

    float a = 0.5;
    float f = 0.8;

    for (int i = 0; i < 5; i++) { // 
        noiseValue += a * perlin(p * f);

        a *= 0.5;
        f *= 2.0;
    }

    return noiseValue;
}

float fbm4_w(vec2 p){
    float noiseValue = 0.0;

    float a = 0.5;
    float f = 0.8;

    for (int i = 0; i < 5; i++) { // 
        noiseValue += a * worley(p * f);

        a *= 0.5;
        f *= 2.0;
    }

    return noiseValue;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= scale;
    uv -= vec2(.5);
    uv.x *= iResolution.x / iResolution.y;

    float p = perlin(uv);
    float w = worley(uv);

    float _p_fbm4 = fbm4_p(uv);
    float _w_fbm4 = fbm4_w(uv);
    float pw = clamp(remap(_p_fbm4,_w_fbm4,1.0,0.0,1.0),0.,1.);

	fragColor =  vec4(pw,pw,pw,1.);
	//fragColor =  vec4(p,p,p,1.);
	//fragColor =  vec4(w,w,w,1.);
}

