#define bluenoiseWidth  64
#define bluenoiseHeight 64
#define sigma           1.9


#define log2e           1.4426950408889634073599246810018

const int maxIterations = bluenoiseWidth * bluenoiseHeight - 1;
const float valueMultiplier = 1.0 / float(maxIterations);
const float expMultiplier = 1.0 / (sigma * sigma) * log2e;
const vec4 textureSizeAndInvSize = vec4(float(bluenoiseWidth),
                                        float(bluenoiseHeight),
                                        1.0 / float(bluenoiseWidth),
                                        1.0 / float(bluenoiseHeight));


uint simpleHash32(uvec3 Seed)
{
    uint hx = (0xb543c3a6u ^ Seed.x);
    uint hy = (0x526f94e2u ^ Seed.y);
    uint hxy = hx * hy;
    uint hz0 = 0x53c5ca59u ^ (hxy >> 5u);
    uint hz1 = (0x74743c1bu ^ Seed.z);
    uint h = hz0 * hz1;
    return h;
}

