// This stage deals with updating the blue noise energy.
// On the first iteration, we simply seed it with background
// energy.


// Uses the last 23bits to construct a (non-linear) range
// [0, 1.17549421069e-38]
float backgroundEnergyBounded(uint seed)
{
    return uintBitsToFloat(seed & 0x007fffffu);
}

float getBackgroundEnergy()
{
    uint backgroundEnergySeed = simpleHash32(floatBitsToUint(iDate.yzw)) ^ floatBitsToUint(iTime);
    uint S = simpleHash32(uvec3(uvec2(gl_FragCoord.xy), uint(backgroundEnergySeed)));
    return backgroundEnergyBounded(S);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    if(any(greaterThanEqual(gl_FragCoord.xy, textureSizeAndInvSize.xy)))
    {
        return;
    }

    if(iFrame == 0)
    {
        fragColor = vec2(0., getBackgroundEnergy()).xyxx;
        return;
    }

    vec2 prevValue = texelFetch(iChannel0, ivec2(gl_FragCoord.xy), 0).xy;
    if(iFrame > maxIterations)
    {
        fragColor = prevValue.xyxx;
        return;
    }

    // Get the result from downscaling and finding the smallest void
    float value = 1.0 - (valueMultiplier * float(iFrame - 1));
    vec2 target = texelFetch(iChannel1, ivec2(0, 0), 0).yz;
    vec2 coord = vec2(floor(gl_FragCoord.xy));
    float noise = all(equal(target, coord)) ? value : 0.;

    // Wrap around logic
    vec2 delta = fract(abs(coord - target) * textureSizeAndInvSize.zw);
    delta = 0.5 - abs(delta - 0.5);
    delta *= textureSizeAndInvSize.xy;
    float energy = exp2(-dot(delta, delta) * expMultiplier) * value;

    noise += prevValue.x;
    energy += prevValue.y;

    fragColor = vec2(noise, energy).xyxx;

}

