vec3 fractal(vec2 c, vec2 c2) {	
	vec2 z = c;
	float ci = 0.0;
	float mean = 0.0;
	for(int i = 0;i < 64; i++) {
		vec2 a = vec2(z.x,abs(z.y));
		float b = atan(a.y, a.x);
		if(b > 0.0) b -= 6.283185307179586;
		z = vec2(log(length(a)),b) + c2;
		if (i>5) mean+=length(z);
	}
	mean/=float(62);
	ci =  1.0 - log2(.05*log2(mean/1.));
	return vec3( .5+.5*cos(6.*ci+0.0),.4+.5*cos(66.*ci + 0.4),.5+.5*cos(6.*ci +1.7) );
}


