// Contains the function to display.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    // Only process the first row of pixels.
    if(fragCoord.y > 0.5)
    {
        return;
    }

    // 1-D perlin noise.
    const int maxLayer = 5;
    for(int i = 2; i < maxLayer; i++)
    {
        vec2 uv = fragCoord  / iChannelResolution[1].xy;

        // Randomize offset per layer.
        float uvOffset = float(i) * 1.618;
        uvOffset -= floor(uvOffset);

        // Get weighted sample with varying scale.
        float weightScale = float(1 << i);
        float uvScale = 1.0 / weightScale;
        uv.y += iTime * 0.05;
        uv *= uvScale;
        uv += vec2(uvOffset);
        vec4 t = texture(iChannel1, uv * uvScale);
        t.a = 1.0;
        t *= weightScale;

        fragColor += t;
    }

    fragColor.rgb /= fragColor.a;

    // Add contrast.
    fragColor -= vec4(0.5);
    fragColor *= fragColor.a / float(1 << (maxLayer-1));
    //fragColor = tanh(fragColor);
    //fragColor *= 0.5;
    fragColor += 0.5;
}

