// Contains the function to display.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 intFragCoord = ivec2(fragCoord);

    if(fragCoord.y > 0.5)
    {
        fragColor = vec4(0);
        return;
    }

    float mouseY = iMouse.y < 0.5 ? iResolution.y * 0.5 : iMouse.y; 

    // The X center of "my" pixel (the current one being drawn).
    float myCenter = float(intFragCoord.x) + 0.5;
    float myHeight = texelFetch(iChannel0, ivec2(fragCoord.x, 0), 0).r;

    float dither = mouseY / iResolution.y;
    vec4 thresh = (vec4(dither));
    float maxDist = iResolution.x;
    vec4 dist = vec4(maxDist);
    bvec4 mySign = greaterThan(vec4(myHeight), thresh);

    ivec2 intResolution = ivec2(iResolution.xy);
    for(int iNeighbor = 0; iNeighbor < intResolution.x; iNeighbor++)
    {
        // This doesn't work for some reasosn.
        /*int neighborX = (iNeighbor / 2) + 1;
        if((iNeighbor % 2) == 0)
            neighborX = -neighborX;

        neighborX += intFragCoord.x;

        if(neighborX > intResolution.x-1) neighborX = intResolution.x-1;
        if(neighborX < 0) neighborX = 0;*/

        // Can't break early, I guess. Brute force it is. -shrug-
        int neighborX = iNeighbor;

        // The X center of the neighbor pixel.
        float neighborCenter = float(neighborX) + 0.5;
        float neighborNearestEdge = neighborCenter + sign(myCenter - neighborCenter) * 0.5;

        float distMeToNeighbor = abs(myCenter - neighborNearestEdge);

        float neighborHeight = texelFetch(iChannel0, ivec2(neighborX, 0), 0).r;


        bvec4 neighborSign = greaterThan(vec4(neighborHeight), thresh);

        bvec4 signEquals = equal(mySign, neighborSign);
        bvec4 neighborFurther = greaterThanEqual(vec4(distMeToNeighbor), dist);

        dist.x = signEquals.x || neighborFurther.x ? dist.x : distMeToNeighbor;
        dist.y = signEquals.y || neighborFurther.y ? dist.y : distMeToNeighbor;
        dist.z = signEquals.z || neighborFurther.z ? dist.z : distMeToNeighbor;
        dist.w = signEquals.w || neighborFurther.w ? dist.w : distMeToNeighbor;
    }

    dist *= vec4(mySign) * 2.0 - vec4(1.0);
    dist /= vec4(maxDist);
    dist = dist * 0.5 + 0.5;

    fragColor = vec4(dist);


}

