#define ROT(theta) mat2(cos(theta), sin(theta), -sin(theta), cos(theta))

float modTag(ivec2 px) {
    px = abs(px);
    int x = px.x, y = px.y;
    return sign(float(
        (x ^ y) % 5 // your expression here!
    ));
}

