   // vec2 pixelColor = step(grayScaleValue+0.5,imagePixel.xy);
    vec2 pixelColor = smoothstep(0.,grayScaleValue+1.,imagePixel.xy);

    fragColor=vec4(pixelColor,imagePixel.b,0.);

}

