        fragColor = vec4(0.0,0.0,0.0,1.0);
        return;
    }

    vec2 a_n_mouse_normalized = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);

    vec2 a_n_pixel_coordinate_normalized = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);

