    if(
        a_n_pixel_coordinate_normalized.x >= a_n_mouse_normalized.x
        &&
        a_n_pixel_coordinate_normalized.x < a_n_mouse_normalized.x + n_scale_x_normalized
        &&
        a_n_pixel_coordinate_normalized.y >= a_n_mouse_normalized.y
        &&
        a_n_pixel_coordinate_normalized.y < a_n_mouse_normalized.y + n_scale_y_normalized
     ){
        float n_center_x = (a_n_mouse_normalized.x + (n_scale_x_normalized / 2.0));
        float n_center_y = (a_n_mouse_normalized.y + (n_scale_y_normalized / 2.0));
