const vec3 lPos = vec3(0, 3, 0);
const float PI = 3.1415926535;
const float degreesToRadians = PI/180.0;
const float FOV = 70.0*degreesToRadians;
const float fogDensity = 3.0;
const vec3 fogColor = vec3(0.4, 0.7, 0.9);

const float f = 1.0/tan(FOV/2.0);
const float aperture = 0.015;
const float focalLength = 11.1;

struct material{
    vec3 col;
    float type;
    float intensity;
};

struct box{
    vec3 pos;
    vec3 size;
    material mat;
};

struct camera{
    vec3 o;
    vec3 d;
};

box[] scene = box[](
    box(vec3(0, 0, 0), vec3(500, 1, 500), material(vec3(1), 3.0, 0.8)),
    box(vec3(-2, 1, 2), vec3(2, 1, 1), material(vec3(1, 1, 0), 3.0, 0.6)),
    box(vec3(2, 1.5, 6), vec3(1, 2, 1), material(vec3(1, 0, 1), 3.0, 0.4)),
    box(vec3(-3, 1.5, 0), vec3(2), material(vec3(0, 1, 0), 3.0, 0.9)),
    box(vec3(4, 1.25, 1), vec3(1.5), material(vec3(0, 0, 1), 3.0, 0.7)),
    box(vec3(0, 1, 2), vec3(1), material(vec3(1, 0, 0), 3.0, 0.5)),
    box(vec3(-2.5, 3, 0), vec3(1), material(vec3(1, 0.5, 0), 3.0, 0.2)),
    box(lPos, vec3(1, 1, 1), material(vec3(1), 0.0, 0.5))
);

bool raytraceBox(vec3 o, vec3 d, out float t, box b, out material mat){
    vec3 tMin;
    vec3 tMax;

    vec3 halfSize = b.size/2.0;

    vec3 lbf = b.pos - halfSize;
    vec3 rtb = b.pos + halfSize;

    tMin = (lbf - o)/d;
    tMax = (rtb - o)/d;

    if(tMin.x > tMax.x){
        float tempT = tMin.x;
        tMin.x = tMax.x;
        tMax.x = tempT;
    }

    if(tMin.y > tMax.y){
        float tempT = tMin.y;
        tMin.y = tMax.y;
        tMax.y = tempT;
    }

    if(tMin.z > tMax.z){
        float tempT = tMin.z;
        tMin.z = tMax.z;
        tMax.z = tempT;
    }

    if((tMin.x > tMax.y) || (tMin.y > tMax.x)){
        return false;
    }

    if(tMin.y > tMin.x){
        tMin.x = tMin.y;
    }

    if(tMax.y < tMax.x){
        tMax.x = tMax.y;
    }

    if((tMin.x > tMax.z) || (tMin.z > tMax.x)){
        return false;
    }

    if(tMin.z > tMin.x){
        tMin.x = tMin.z;
    }

    if(tMax.z < tMax.x){
        tMax.x = tMax.z;
    }

    mat = b.mat;
    t = tMin.x;

    return true;
}

bool raytraceScene(vec3 o, vec3 d, out float t, out material mat, out int Index){
    float initT = 1000000000.0;
    bool r;
    int index;
    for(int i = 0; i < scene.length(); i++){
        float testT;
        bool rt = raytraceBox(o, d, testT, scene[i], mat);
        if(rt && testT < initT && testT >= 0.0){
            r = true;
            initT = testT;
            index = i;
        }
    }
    t = initT;
    Index = index;
    mat = scene[Index].mat;
    return r;
}

vec3 getBoxNormals(vec3 h, box b){
    vec3 n;

    vec3 p = h;

    vec3 halfSize = (b.size*0.99999)/2.0;

    vec3 lbf = (b.pos - halfSize);
    vec3 rtb = (b.pos + halfSize);

    if(p.x >= rtb.x){
        n = vec3(1, 0, 0);
    }
    if(p.x <= lbf.x){
        n = vec3(-1, 0, 0);
    }
    if(p.y >= rtb.y){
        n = vec3(0, 1, 0);
    }
    if(p.y <= lbf.y){
        n = vec3(0, -1, 0);
    }
    if(p.z >= rtb.z){
        n = vec3(0, 0, 1);
    }
    if(p.z <= lbf.z){
        n = vec3(0, 0, -1);
    }

    return normalize(n);
}

float getLighting(vec3 h, vec3 lpos, material inMat, int index){
    float brightness = 1.0;

    float lt;
    material lmat;
    int i;
    vec3 lray = lpos - h;

    bool rl;

    if(inMat.type != 0.0){
        rl = raytraceScene(h, lray, lt, lmat, i);
    }else{
        rl = false;
    }

    vec3 n = getBoxNormals(h, scene[index]);
    //if(lmat.type != 0.0){
        brightness = max(dot(normalize(lray), n), 0.1);
    //}else{
        //brightness = 1.0;
    //}

    float spec = pow(max(dot(normalize(lray), n), 0.1), 15.0);

        if(rl && scene[index] != scene[i]){
            if(lmat.type != 0.0){
                spec = 0.0;
                brightness *= 0.4;
            }else if(lmat.type == 0.0){
                brightness = max(dot(normalize(lray), n), 0.1);
            }
        }else{
            if(lmat.type != 0.0){
                spec = pow(max(dot(normalize(lray), n), 0.1), 15.0);
                brightness = max(dot(normalize(lray), n), 0.1);
            }else if(lmat.type == 0.0){
                brightness = 1.0;
            }
        }

    if(inMat.type == 2.0){
        brightness += spec;
    }

    return brightness;
}

vec3 reflectRay(vec3 h, vec3 d, int index){
    vec3 n = getBoxNormals(h, scene[index]);
    return d - 2.0*n*dot(d, n);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec3 initCol = vec3(0);
    vec3 col = fogColor;
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    //scene[1].pos.x += sin(iTime*0.5)*3.0 + 4.0;
    //scene[1].pos.z += sin(iTime*0.5)*3.0 - 2.0;
    mat3 projection;
    projection[0] = vec3(1, 0, 0);
    projection[1] = vec3(0, 1, 0);
    projection[2] = vec3(0, 0, f);

    camera cam = camera(vec3(5, 5, -10), vec3(20, 20, 0));

    mat3 xRotation;
    xRotation[0] = vec3(1, 0, 0);
    xRotation[1] = vec3(0, cos(cam.d.x*degreesToRadians), -sin(cam.d.x*degreesToRadians));
    xRotation[2] = vec3(0, sin(cam.d.x*degreesToRadians), cos(cam.d.x*degreesToRadians));

    mat3 yRotation;
    yRotation[0] = vec3(cos(cam.d.y*degreesToRadians), 0, -sin(cam.d.y*degreesToRadians));
    yRotation[1] = vec3(0, 1, 0);
    yRotation[2] = vec3(sin(cam.d.y*degreesToRadians), 0, cos(cam.d.y*degreesToRadians));

    vec3 camd = normalize(vec3(uv, 1.0)*projection*xRotation*yRotation);
    vec3 o = cam.o;

    float focusRes = 0.15;

    float iteration = 1.0;

    for(float originMoveX = -1.0; originMoveX < 1.0; originMoveX += focusRes){
        for(float originMoveY = -1.0; originMoveY < 1.0; originMoveY += focusRes){
            o.x += (originMoveX*aperture)/(0.5/focusRes);
            o.y += originMoveY*aperture;
            //if(length(o.xy - cam.o.xy) < aperture/1.0){
            float t;
            material mat;
            int i;
            vec3 focalPoint = cam.o + camd*focalLength;
            vec3 d = normalize(focalPoint - o);

            bool r = raytraceScene(o, d, t, mat, i);

            vec3 n = getBoxNormals(o + d*t, scene[i]);

            float c = getLighting(o + d*t, lPos, mat, i);

            if(r){
                if(mat.type != 3.0){
                    col = mix(mat.col*c, fogColor, min(t/(1.0/(fogDensity*0.004)), 1.0));
                }else{
                    float rt;
                    int ri;
                    material rmat;
                    vec3 rd = normalize(reflectRay(o + d*t, d, i));

                    bool reflectTrace = raytraceScene(o + d*t, rd, rt, rmat, ri);

                    if(reflectTrace){
                        float rc = getLighting((o + d*t) + rd*rt, lPos, rmat, ri);
                        col = mix(mix(rmat.col*rc, mat.col, mat.intensity)*c, fogColor, min(t/(1.0/(fogDensity*0.004)), 1.0));
                    }else{
                        col = mix(mix(fogColor, mat.col, mat.intensity)*c, fogColor, min(t/(1.0/(fogDensity*0.004)), 1.0));
                    }
                }
            }
            iteration++;
            initCol = mix(initCol, col, 1.0/(iteration));
        }
    }

    fragColor = vec4(pow(initCol, vec3(1.0/2.2)), 1);
}

