#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

// RayMarching from TheArtOfCode

// From BlackleMori
#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox(in vec2 p, in vec2 b){
    vec2 d = abs(p)-b;
    return length(max(d,0.))+min(max(d.x,d.y),0.);
}

vec3 rayOri() {
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 ro = vec3(0, 3, -3);
    ro.xz *= rot(-pi/2.);
    ro.yz *= rot(-m.y*3.14+1.);
    ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float dist(vec3 p) {
    float o = 2. * pi / 3.;
    float t = exp(-2. * length(p)) + 0.25 * iTime;
    p.xy *= rot(pi * thc(4., t + o));
    p.yz *= rot(pi * thc(4., t));
    p.zx *= rot(pi * thc(4., t -o));
    float sd = length(p) - 1.6;
    p = erot(p, normalize(vec3(1,2,3)), iTime);
    //p.xz *= rot(-21.4 * iTime);
    //p.xy *= rot(-11.4 * iTime);
    float bd = sdBox(p, vec3(0.75)) - 0.;
    //p.xz *= rot(10.4 * iTime);
    p = erot(p, normalize(vec3(3,2,1)), -1. * iTime);
    float c = 0.5 + 0.5 * cc(1.5, 0. * log(length(p)) + iTime);
    c = pow(4. * c * (1.-c), 2.);
    float bd2 = sdBox(p, vec3(0.3 + 0.15 * c)) - 0.1 * c;//(0.5 - 0.5 * c);
    bd = -min(-bd, bd2);
    return -min(-sd, bd);
}

float march(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = dist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        //dS *= .5 + .5 * h21(vec2(h21(p.xz + iTime),p.y + iTime));
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 normal(vec3 p) {
	float d = dist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        dist(p-e.xyy),
        dist(p-e.yxy),
        dist(p-e.yyx));

    return normalize(n);
}

vec3 rayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec3 tx = texelFetch(iChannel0, ivec2(fragCoord - 0. * vec2(2,3)), 0).rgb;//texture(iChannel0, uv).rgb;
    vec3 ro = rayOri();

    vec3 rd = rayDir(uv, ro, vec3(0), 1.25);
    vec3 col = vec3(0);

    float d = march(ro, rd, 1.);

    vec3 p = ro + rd * d;
    float IOR = 1.2;
    if(d<MAX_DIST) {        
        vec3 n = normal(p);
        vec3 r = reflect(rd, n);

        vec3 q = p - 4. * SURF_DIST * n;
        vec3 rd2 = refract(rd, n, 1./IOR);
        float d2 = march(q, rd2, -1.);       
        vec3 p2 = q + d2 * rd2;
        vec3 n2 = -normal(p2); 

        vec3 q2 = p2 - 4. * SURF_DIST * n2;
        vec3 rd3 = refract(rd2, n2, 1./IOR);
        float d3 = march(q2, rd3, 1.);       
        vec3 p3 = q2 + d3 * rd3;
        vec3 n3 = normal(p3);

        vec3 lightDir = normalize(vec3(1,2,3));//normalize(vec3(cos(iTime),0.5 * sqrt(2.),sin(iTime)));
        float dif  = dot(n,  lightDir)*.5+.5;
        float dif2 = dot(n2, lightDir)*.5+.5;
        float dif3 = dot(n3, lightDir)*.5+.5;       

        float fres  = pow(1. + dot(rd, n),  5.);
        float fres2 = pow(1. + dot(rd, n2), 5.);
        float fres3 = pow(1. + dot(rd, n3), 5.); // hmm

        //dif2 = pow(4. * dif2 * (1.-dif2), 8.);
        //dif3 = pow(4. * dif3 * (1.-dif3), 8.);

        float spec  = pow(dif,  32.);
        float spec2 = pow(dif2, 32.);
        float spec3 = pow(dif3, 32.);

        float t = iTime;
        float th = pi * h21(t + vec2(h21(t + p.xy), p.z));
        float th2 = 2. * pi * h21(t + vec2(h21(t - p.xy), -11. + p.z));
        float m = 1.;
        vec3 w = length(p3) * vec3(cos(th) * sin(th2), sin(th) * sin(th2), cos(th2));
        col = 0.5 + 0.5 * normalize(p3 - w);
        col += 0.5 * dif3;
        float test = abs(dot(n3,n2));
        // dividing by zero is fun
        col /= 0.5 + 0.5 * thc(8., 4. * cross(p2,p3).y);
        col *= clamp((dif3 + spec3) * vec3(test), 0., 1.);


        //col *= 0.5 + 0.5 * thc(4., 2. * iTime);
        col = clamp(col, 0., 1.);
        col *= step(length(p3), MAX_DIST);
        col = 4. * col * (1.-col);
        col = pow(col, vec3(0.5));
        col *= 0.8 + 0.4 * h21(uv + mod(iTime, 301.));
    }

    col = pow(col, vec3(.4545));	// gamma correction
    //col = mix(tx, col, exp(-0.5 * iTime));
    //col = normalize(col);
    col = mix(tx, col,0.25);//exp(-0.5 * iTime));

    fragColor = vec4(col,1.0);
}

