#define STEPS 100. // set higher for coolness
#define BLUR_OUTWARDS
//#define ADD_ORIGINAL

float hash21(in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float dither(float c, float b, vec2 p)
{
    float s = floor(c*b)/b;
    float r = fract(c*b);
    return s + float(hash21(p) < r)*r/b;
}

vec4 dither(in vec4 c, float b, vec2 p)
{
    for(int s = 0; s < 4; s++)
    {
        c[s] = dither(c[s], b, p);
    }
    return c;
}

vec4 ghostBlur(sampler2D tex, vec2 p, float it)
{
    vec4 res = vec4(0);
    vec2 np;
    float st = 2./it;
    for(float i = 1.; i > -1.-st; i -= st)
    {
        #ifdef BLUR_OUTWARDS
        np = (p-0.5) * i + 0.5;
        #else
        np = (p-0.5) / i + 0.5;
        #endif
        res += texture(tex, np);
    }
    return res / it
    #ifdef ADD_ORIGINAL 
    + texture(tex, p)
    #endif
    ;
}

