// CC0: F# logo distance field
//  Apart from shader coding I enjoy tinkering in F#
//  Recreating the F# logo as a shader seemed like a good idea.

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float triangleIsosceles(vec2 p, vec2 q ) {
  p.x = abs(p.x);
  vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
  vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
  float s = -sign( q.y );
  vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                vec2( dot(b,b), s*(p.y-q.y)  ));
  return -sqrt(d.x)*sign(d.y);
}

// "Better"
vec2 dfsharp0(vec2 p) {
  const float sm = 0.005;
  const mat2 rot45 = ROT(PI/4.0);
  vec2 p0 = p;
  p0.x = -abs(p0.x);
  p0.x += 0.7;
  p0 = p0.yx;
  vec2 p1 = p;
  p1 *= rot45; 
  vec2 p2 = p;
  p2.x += 0.26;
  p2 = p2.yx;
  float d0 = triangleIsosceles(p0, 1.35*vec2(0.5, 0.5)-sm);
  float d1 = box(p1, vec2(0.26)+sm);
  float d2 = triangleIsosceles(p2, 0.47*vec2(0.5, 0.5)-sm);
  float d = d0;
  d = pmax(d, -d1, sm);
  d = pmin(d, d2, sm);
  return vec2(d-sm, p.x > 0.0 ? 1.0 : 0.0);
}

// "Cheaper"
vec2 dfsharp1(vec2 p) {
  const mat2 rot45 = ROT(PI/4.0);
  vec2 p0 = p;
  p0 *= rot45;
  vec2 p2 = p;
  float d0 = box(p0, vec2(0.5));
  float d1 = d0+0.275;
  float d2 = abs(d1)-0.035;
  float d3 = -p2.x-0.025;
  d2 = min(d2, max(d1, d3));
  float d4 = abs(p2.x)-0.025;
  float d = d0;
  d = max(d, -d2);
  d = max(d, -d4);
  return vec2(d, p.x > 0.0 ? 1.0 : 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float aa = 2.0/RESOLUTION.y;
  vec2 d = dfsharp1(p);
  vec3 col = vec3(0.00);
  vec3 bcol0 = vec3(1.0, 0.0, 0.5).yzx;
  vec3 bcol1 = bcol0.yzx;;
  col = mix(col, mix(bcol0, sqrt(bcol0), d.y), smoothstep(aa, -aa, d.x));
  col = sqrt(col);
  fragColor = vec4(col, 1.0);
}

