*/

#define R   iResolution
#define M   iMouse
#define T   iTime

#define PI  3.1415926

mat2 rot (float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float hash21(vec2 a){ return fract(sin(dot(a,vec2(22.34,35.34)))*483434.);}

//globals
mat2 trot;
float glow=0.,tglow=0.;

const float ofx = 1.25;

float sdGry(vec3 p, float s, float t, float b) {
    p *= s;
    return abs(dot(sin(p*ofx),cos(p.zxy))-b)/(s*ofx)-t;
}

vec2 map(vec3 p) {
    vec2 res =vec2(1e5,1.);

    vec3 q = p;
    float tt = .5+.5*sin(T*.2);

    float k = 8./dot(p,p);
    float mul = 1./k;
    p*=k;

    p.xy*= trot;
    p.yz += vec2(-1.25, T);

    float d = .365, mf = 1e5;
    float mm = .25+.25*sin(T*.75);

    for(float j=-2.;j<2.;j++){
        vec3 nf =p;
        nf.z=round(nf.z/d+j)*d;
        float ids = mod(nf.z,2.);

        float fd= sdGry(nf, .725, .05, .65);

        nf.z=clamp(p.z,nf.z-d/4.,nf.z+d/4.);
        fd=length(vec2(max(.0,fd), nf.z-p.z));

        float idx = mod(nf.z,5.);

        if(idx<mm+.025&&idx>mm) tglow+=.002/(.015+fd*fd);
        if(ids<mm+.075&&ids>mm) glow+=.002/(.015+fd*fd);

        mf=min(mf,fd);
    }
    if(mf<res.x) res=vec2(mf,3.);

    res.x*= mul/1.35;
    return res;
}

void mainImage( out vec4 O, in vec2 F ) {

    trot=rot(T*.072);

    vec2 uv = (2.* F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(0,0,.5);
    vec3 rd = normalize(vec3(uv, -1));

    float x = M.xy == vec2(0) || M.z < 0. ? 0. : -(M.y/R.y * .5 - .25) * PI;
    float y = M.xy == vec2(0) || M.z < 0. ? 0. : -(M.x/R.x * 2. - 1.) * PI;

    mat2 rx = rot(x),ry = rot(y);

    ro.yz *= rx;ro.xz *= ry;
    rd.yz *= rx;rd.xz *= ry;

    vec3 C = vec3(0);
    vec3 p = ro;

    float d=0.,m=0.;
    for(int i=0;i<164;i++){
        vec2 t = map(p);
        d += i<64? t.x*.35:t.x*.75;
        m  = t.y;  
        p = ro + rd * d;
        if(t.x<d*1e-4||d>45.) break;
    } 

    float sp = .2+.2*sin(uv.x*4.1+T);
    vec3 fog = mix(vec3(0.043,0.153,0.255),vec3(0.235,0.302,0.000),clamp((uv.y+.5-sp),0.,1.));

    C = mix(C,vec3(glow,glow*.65,glow*.15),clamp(glow,.0,.6));
    C = mix(C,vec3(tglow*.15,tglow*.465,tglow),clamp(tglow,.0,.8));

    C = mix(C,fog, 1.-exp(-20.*d*d*d));

    C = clamp(C,vec3(0),vec3(1));
    O = vec4(pow(C, vec3(.4545)),1);
}

const float PI = 3.1415926;

float reuleaux(vec2 p, int n) {
    float a = atan(p.y, p.x);
    float ang = PI/float(n);
    float cc = cos(a - 2.0 * ang * floor(0.5 * (a - PI) / ang + 0.5));
    return length(p) - (cc + sqrt(1.0 + 2.0 * cos(ang) + cc * cc));
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {   
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    float rlp = reuleaux(uv, int(iTime * 2.0) % 4 + 3);
    float rl = smoothstep(0.0, -length(fwidth(uv)) * 2.0, rlp);
    fragColor = vec4(vec3(rl), 1.0);
}

#define PI 3.14159265359
#define selec 12

float N21(in vec2 p)
{
    p = 50.*fract( p * PI);
    p = 50.*fract((p+vec2(0.24342,709.23))/PI);

    return -1. + 2.*fract((p.x*p.y) );
}

vec2 N22(in vec2 p)
{
    p = 50.*fract((p+vec2(0.24342,709.23))/PI);
    return 1. - 2.*fract(p * vec2(p.x+p.y, p.y*p.x) + vec2(0.29137,0.9348));
}

vec2 modulo(in vec2 divident, in vec2 divisor)
{
   return mod(divident,divisor) * (divisor / abs(divisor));
}

float valueNoise(in vec2 pos)
{
    vec2 i = floor(pos);
    vec2 f = fract(pos);

    vec2 inter = f * f * (3. -2.*f);

    float vA = N21(i + vec2(0.,0.));
    float vB = N21(i + vec2(1.,0.)); 
    float vC = N21(i + vec2(0.,1.)); 
    float vD = N21(i + vec2(1.,1.)); 

    return vA + (vB - vA)*inter.x + (vC - vA)*inter.y + (vD - vC - vB + vA) * inter.x * inter.y;
}

float tiledValueNoise(in vec2 pos, in vec2 frec)
{
    vec2 f = fract(pos);

    vec2 imin = floor(pos);
    vec2 imax = ceil(pos);

    imin = modulo( imin, frec );
    imax = modulo( imax, frec );


    vec2 inter = f * f * (3. -2.*f);

    float vA = N21( imin );
    float vB = N21( vec2( imax.x, imin.y)); 
    float vC = N21( vec2( imin.x, imax.y)); 
    float vD = N21( imax ); 

    return vA + (vB - vA)*inter.x + (vC - vA)*inter.y + (vD - vC - vB + vA) * inter.x * inter.y;
}

float gradientNoise(in vec2 pos)
{
    vec2 f = fract(pos);
    vec2 i = floor(pos);

    vec2 inter = f * f * ( 3. - 2. * f);

    vec2 vecA = N22( i );
    vec2 vecB = N22( i + vec2( 1. , 0. ) );
    vec2 vecC = N22( i + vec2( 0. , 1. ) );
    vec2 vecD = N22( i + vec2( 1. ));

    float valA = dot( f , vecA );
    float valB = dot( f - vec2(1.,0.) , vecB );
    float valC = dot( f - vec2(0.,1.) , vecC );
    float valD = dot( f - vec2(1.,1.) , vecD );

    return valA + ( valB - valA ) * inter.x + ( valC - valA ) * inter.y + ( valD - valB - valC + valA ) * inter.x * inter.y;

}

float tiledGradientNoise(in vec2 pos, in vec2 frec)
{
    vec2 f = fract(pos);
    vec2 imin = floor(pos);
    vec2 imax = ceil(pos);

    imin = modulo(imin, frec);
    imax = modulo(imax, frec);

    vec2 inter = f * f * ( 3. - 2. * f);

    vec2 vecA = N22( imin );
    vec2 vecB = N22( vec2( imax.x, imin.y) );
    vec2 vecC = N22( vec2( imin.x, imax.y) );
    vec2 vecD = N22( imax );

    float valA = dot( f , vecA );
    float valB = dot( f - vec2(1.,0.) , vecB );
    float valC = dot( f - vec2(0.,1.) , vecC );
    float valD = dot( f - vec2(1.,1.) , vecD );

    return valA + ( valB - valA ) * inter.x + ( valC - valA ) * inter.y + ( valD - valB - valC + valA ) * inter.x * inter.y;

}

float valueFBM(in vec2 p, in float h, in int octaves)
{

    float value = 0.;

    for(int i = 0; i < octaves ; i++ )
    {
        float f = pow(2.,float(i));
        float a = pow(f,-h);
        value += a * valueNoise( f*p );
    }

    return value;
}

float tiledValueFBM(in vec2 p, in vec2 frec ,in float h, in int octaves)
{

    float value = 0.;

    for(int i = 0; i < octaves ; i++ )
    {
        float f = pow(2.,float(i));
        float a = pow(f,-h);
        value += a * tiledValueNoise( f*p , frec);
    }

    return value;
}

float gradientFBM(in vec2 p, in float h, in int octaves)
{

    float value = 0.;

    for(int i = 0; i < octaves ; i++ )
    {
        float f = pow(2.,float(i));
        float a = pow(f,-h);
        value += a * gradientNoise( f*p );
    }

    return value;
}

float tiledGradientFBM(in vec2 p, in vec2 frec ,in float h, in int octaves)
{

    float value = 0.;

    for(int i = 0; i < octaves ; i++ )
    {
        float f = pow(2.,float(i));
        float a = pow(f,-h);
        value += a * tiledGradientNoise( f*p , frec);
    }

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    // Polar Coordinates
    vec2 p_uv = vec2( atan(uv.x,uv.y), length(uv));
    // Normalized Polar Coordinates
    vec2 np_uv = vec2( p_uv.x/(2.*PI)+0.5 , p_uv.y );

    float noise = 0.;
#if selec == 0
