#elif selec == 3
    noise = clamp(tiledValueNoise(np_uv*vec2(5.,5.)+vec2(iTime), vec2(5.)),0.,1.);
    noise *= tiledValueNoise(np_uv*vec2(40.,0.)+vec2(iTime,0.), vec2(20.));
#elif selec == 4
    noise = gradientNoise(uv*10.+vec2(iTime));
#elif selec == 5
    noise = tiledGradientNoise(uv*3.+vec2(iTime), vec2(10.,5.) );
#elif selec == 6
    noise = clamp(tiledGradientNoise(np_uv*vec2(5.,5.)+vec2(iTime*0.5,iTime), vec2(5.)),0.,1.);
#elif selec == 7
    noise = 0.7 * valueFBM(uv*10.+vec2(iTime),0.49,4);
#elif selec == 8
    noise = 0.7 * tiledValueFBM(uv*10., vec2(10.) ,0.49,4);
#elif selec == 9
    noise = 0.7 * tiledValueFBM(np_uv*vec2(10.,2.)+vec2(iTime*0.5), vec2(10.) ,0.49,7);
    noise *= np_uv.y;
#elif selec == 10
    noise = 0.7 * gradientFBM(uv*10.+vec2(iTime),0.49,5);
