vec2 moveUv(vec2 _uv){
    float tension = abs(pow(sin(iTime), 2.));
    return vec2(
        _uv.x / 1.5 - (cos(iTime) * .125 - .125),
        (_uv.y + tension * sin(_uv.x * PI * 10.) / 50. ) / 1.5 + (cos(iTime) * .125 + .125)
    );
}

vec2 distort(vec2 _uv){
    return vec2(_uv.x / 1.5 + sin(_uv.y) * cos(_uv.x), _uv.y / 1.5 - cos(_uv.y) * sin(_uv.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
