    float threshold = -1. * cos(iTime);
    vec2 movedUv = moveUv(uv);
    vec4 tex_a = texture(iChannel0, mix(distort(uv), movedUv, smoothstep(threshold, threshold + _t, uv.x - uv.y))),
         tex_b = texture(iChannel1, mix(movedUv, distort(uv), smoothstep(threshold - _t,threshold,uv.x - uv.y)));


    // Output to screen
    fragColor = mix(tex_a, tex_b, step(uv.x - uv.y, threshold));
}

mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy -.5 *iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    float aa=0.,rnd;
    for(aa=0.;aa++<4.;){
        vec3 p,d=normalize(vec3(uv,1.));
        for(float i=0.,e,g=0.;i++<99.;){
            vec3 p = d*g,op=p;
            p.xy+=rnd=sqrt(i)*fract(tan(445.887*sin(dot(vec2(p.xy)*aa,vec2(2425.223,984.401)))))*.03*length(uv);
            p.xz*=rot(iTime*.1+asin(sin(iTime+cos(iTime*2.)))*.5);

            p.zx +=iTime;
            p.y -= -2.5+clamp(abs(mod(p.z+p.x,6.)-3.)*.5,0.5,1.5);

            p.xz = fract(p.xz)-.5;
            float h =min(length(p.yz),length(p.xy))-.01;
            g+=e=max(.0001,abs(h)*.7);
            col += sqrt(vec3(.7+sin(iTime+op.z)*.5,.5,.5+cos(iTime-op.z*4.)*.2)*.025/exp(i*i*e));
        }
    }
    fragColor = vec4(sqrt(col/aa),1.0);
}

#define PI 3.14159

float GaborNoise(vec2 uv, vec2 omega, float freq, vec2 orig, float bandwidth){
    float f = 2.*PI*freq; // frequency
    float a = exp(-length(orig-uv)*bandwidth*PI);
    return a*cos(f* dot(uv, omega));
}

vec2 GaborAmplitude(vec2 uv, vec2 omega, float freq, vec2 orig, float bandwidth){
    float f = 2.*PI*freq; // frequency
    float a = exp(-length(orig-uv)*bandwidth*PI);
    return a*vec2(cos(f* dot(uv, omega)),
                sin(f* dot(uv, omega))); // redundancy with GaborNoise for clarity

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv.x /= 1.;
    // Time varying pixel color
    float gn;
    int N = 100;
    vec2 L = vec2(0.);

    float my = iMouse.y/iResolution.y;
    if (my == 0.){
        my = .5;
    }

    float mx = iMouse.x/iResolution.x;
    if (mx == 0.){
        mx = .5;
    }

    float phase_velocity = 2.*mx;
    float freq = 5.;
    float bandwidth = .01;
    for (int i=0; i<N; i++){
        vec2 pos = vec2(noise(vec2(float(i), 0.)), noise(vec2(float(i), 1.)));
        float omega_ = atan(noise(vec2(float(i), 1.)), noise(vec2(float(i), 2.)))*phase_velocity + iTime/20.;
        vec2 omega = vec2(cos(omega_), sin(omega_));


        freq = 40.*my; // *hash11(float(i)); //   one can take varying frequencies and bandwidth; 
        bandwidth = .01; //  * hash11(float(i)+.3);    //

        L += GaborAmplitude((uv-pos), omega, freq, uv, bandwidth);
        gn += GaborNoise((uv-pos), omega, freq, uv, bandwidth);
    }
    float amplitude = length(L);
    float pn = gn/amplitude;
    gn /= sqrt(float(N)); // for high bandwidth i need to tune the normalizing factor
    // Output to screen 
    float an = amplitude/sqrt(float(N));
    pn = mix(pn, an*.5, step(0., uv.y));
    vec3 col = vec3(
                mix(gn, pn, step(0., uv.x))) + 100.*vec3(1.,0.,0.)*smoothstep(0., .005, .005-abs(uv.x))
                + 100.*vec3(1.,0.,0.)*smoothstep(0., .005, .005-abs(uv.y))*step(0., uv.x);

    fragColor = vec4(col, 1.0);
}

common_code:
float hash11( float x){
    return fract( sin( float(x)*11.45067)*41.234);
}


vec2 hash22(vec2 p){
    vec2 a = vec2(110.487,9.4567);
    vec2 b = vec2(4506.775, 50.2467);
    return fract( sin(vec2(dot(a,p), dot(b,p)))*41.234);
}

float hash21(vec2 p){
    vec2 a = vec2(110.487,9.4567);
    return fract( sin(dot(a,p))*41.234);
}

vec2 hash12(float x){
    vec3 p = vec3(x,x,x);
    vec3 a = vec3(10.487,9.4567, 41.28753);
    vec3 b = vec3(456.775, 150.2467, 234.4565);
    return fract( sin(vec2(dot(a,p), dot(b,p)))*41.234);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    float a = hash21(i);
    float b = hash21(i + vec2(1.0, 0.0));
    float c = hash21(i + vec2(0.0, 1.0));
    float d = hash21(i + vec2(1.0, 1.0));
    vec2 u = f*f*(3.0-2.0*f);
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

