#define PI 3.1415926
#define AA 4
#define num 5
vec2 fixUV(in vec2 uv)
{
    return (2.*uv.xy-iResolution.xy)/min(iResolution.x,iResolution.y);
}

vec3 BGColor(in vec2 uv)
{
    vec3 color = vec3(0.);
    vec2 grid = floor(mod(uv,2.));
    if(grid.x != grid.y) color = vec3(0.45);
    else color = vec3(0.55);
    color = mix(color,vec3(0.),smoothstep(1.5*fwidth(uv).x,0.,abs(uv.x)));
    color = mix(color,vec3(0.),smoothstep(1.5*fwidth(uv).y,0.,abs(uv.y)));
    return color;
}

float function(in vec2 uv, in float R)
{
    float x = 3.*uv.x;
    float y = uv.y;
    float r = R+0.5*pow(0.5+0.5*sin(2.*PI*iTime+y/1.),4.);
    float f = r-x*x;
    return f;
}
float functionY(in vec2 uv)
{
    float y = 1.2*uv.y-abs(uv.x)*sqrt((20.*abs(uv.x)/15.));
    y *= 9.*y;
    return y;
}

float plot(vec2 uv){
    float fY = functionY(uv);
    float fuc = function(uv,1.);
    float plotline = 0.;
    for(int i=0;i<num;i++)
    {
        float fuc = function(uv,1.-1.0/float(num)*float(i));
        float line = smoothstep( fuc-0.01, fuc, fY);
        float mark = (-step(sign(mod(float(i),2.)),0.)+step(1.,sign(mod(float(i),2.))));
        plotline = line-plotline*pow(mark,2.);
    }
  return  plotline;
}

