// License CC0: Sea and moon
//  Tinkering with the colors of an old shaders to make it a better fit for windows terminal

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)

const float gravity = 1.0;
const float waterTension = 0.01;

const vec3 skyCol1 = vec3(0.6, 0.35, 0.3).zyx*0.5;
const vec3 skyCol2 = vec3(1.0, 0.3, 0.3).zyx*0.5 ;
const vec3 sunCol1 = vec3(1.0,0.5,0.4).zyx;
const vec3 sunCol2 = vec3(1.0,0.8,0.8).zyx;
const vec3 seaCol1 = vec3(0.1,0.2,0.2)*0.2;
const vec3 seaCol2 = vec3(0.2,0.9,0.6)*0.5;

// License: Unknown, author: Unknown, found: don't remember
float tanh_approx(float x) {
  //  Found this somewhere on the interwebs
  //  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

vec2 wave(in float t, in float a, in float w, in float p) {
  float x = t;
  float y = a*sin(t*w + p);
  return vec2(x, y);
}

vec2 dwave(in float t, in float a, in float w, in float p) {
  float dx = 1.0;
  float dy = a*w*cos(t*w + p);
  return vec2(dx, dy);
}

vec2 gravityWave(in float t, in float a, in float k, in float h) {
  float w = sqrt(gravity*k*tanh_approx(k*h));
  return wave(t, a ,k, w*TIME);
}

vec2 capillaryWave(in float t, in float a, in float k, in float h) {
  float w = sqrt((gravity*k + waterTension*k*k*k)*tanh_approx(k*h));
  return wave(t, a, k, w*TIME);
}

vec2 gravityWaveD(in float t, in float a, in float k, in float h) {
  float w = sqrt(gravity*k*tanh_approx(k*h));
  return dwave(t, a, k, w*TIME);
}

vec2 capillaryWaveD(in float t, in float a, in float k, in float h) {
  float w = sqrt((gravity*k + waterTension*k*k*k)*tanh_approx(k*h));
  return dwave(t, a, k, w*TIME);
}

void mrot(inout vec2 p, in float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

vec4 sea(in vec2 p, in float ia) {
  float y = 0.0;
  vec3 d = vec3(0.0);

  const int maxIter = 8;
  const int midIter = 4;

  float kk = 1.0/1.3;
  float aa = 1.0/(kk*kk);
  float k = 1.0*pow(kk, -float(maxIter) + 1.0);
  float a = ia*0.25*pow(aa, -float(maxIter) + 1.0);

  float h = 25.0;
  p *= 0.5;

  vec2 waveDir = vec2(0.0, 1.0);

  for (int i = midIter; i < maxIter; ++i) {
    float t = dot(-waveDir, p) + float(i);
    y += capillaryWave(t, a, k, h).y;
    vec2 dw = capillaryWaveD(-t, a, k, h);

    d += vec3(waveDir.x, dw.y, waveDir.y);

    mrot(waveDir, PI/3.0);

    k *= kk;
    a *= aa;
  }

  waveDir = vec2(0.0, 1.0);

  for (int i = 0; i < midIter; ++i) {
    float t = dot(waveDir, p) + float(i);
    y += gravityWave(t, a, k, h).y;
    vec2 dw = gravityWaveD(t, a, k, h);

    vec2 d2 = vec2(0.0, dw.x);

    d += vec3(waveDir.x, dw.y, waveDir.y);

    mrot(waveDir, -step(2.0, float(i)));

    k *= kk;
    a *= aa;
  }

  vec3 t = normalize(d);
  vec3 nxz = normalize(vec3(t.z, 0.0, -t.x));
  vec3 nor = cross(t, nxz);

  return vec4(y, nor);
}

vec3 sunDirection() {
  vec3 dir = normalize(vec3(0, 0.06, 1));
  return dir;
}

vec3 skyColor(in vec3 rd) {
  vec3 sunDir = sunDirection();
  float sunDot = max(dot(rd, sunDir), 0.0);
  vec3 final = vec3(0.0);
  final += mix(skyCol1, skyCol2, rd.y);
  final += 0.5*sunCol1*pow(sunDot, 90.0);
  final += 4.0*sunCol2*pow(sunDot, 900.0);
  return final;
}

vec3 render(in vec3 ro, in vec3 rd) {
  vec3 col = vec3(0.0);

  float dsea = (0.0 - ro.y)/rd.y;

  vec3 sunDir = sunDirection();

  vec3 sky = skyColor(rd);

  if (dsea > 0.0) {
    vec3 p = ro + dsea*rd;
    vec4 s = sea(p.xz, 1.0);
    float h = s.x;    
    vec3 nor = s.yzw;
    nor = mix(nor, vec3(0.0, 1.0, 0.0), smoothstep(0.0, 200.0, dsea));

    float fre = clamp(1.0 - dot(-nor,rd), 0.0, 1.0);
    fre = fre*fre*fre;
    float dif = mix(0.25, 1.0, max(dot(nor,sunDir), 0.0));

    vec3 refl = skyColor(reflect(rd, nor));
    vec3 refr = seaCol1 + dif*sunCol1*seaCol2*0.1; 

    col = mix(refr, 0.9*refl, fre);

    float atten = max(1.0 - dot(dsea,dsea) * 0.001, 0.0);
    col += seaCol2*(p.y - h) * 2.0 * atten;

    col = mix(col, sky, 1.0 - exp(-0.01*dsea));

  } else {
    col = sky;
  }

  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 ro = vec3(0.0, 10.0, 0.0);
  vec3 ww = normalize(vec3(0.0, -0.1, 1.0));
  vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize(p.x*uu + p.y*vv + 2.5*ww);

  vec3 col = render(ro, rd);

  fragColor = vec4(col,1.0);
}

// fork of Hatchling's excellent http://shadertoy.com/view/ddS3WR
// swapped distance field extrapolation and ray marcher
// Golfed from 11934 ch down to 4611 so far.
// With some more effort I could probably improve the noise
// and fine tune the shape of the mountains more.
// But I'm pretty happy with it at this point.

#define quat vec4

quat FromAngleAxis(vec3 angleAxis)
{
    float mag = length(angleAxis),
       halfAngle = mag * .5,
       scalar = sin(halfAngle) / max(mag, .00001);
    return vec4(angleAxis * scalar, cos(halfAngle));
}

vec3 qmul(quat q, vec3 v)
{
    vec3 t = 2. * cross(q.xyz, v);
    return v + vec3(q.w * t) + cross(q.xyz, t);
}

quat qmul(quat a, quat b)
{
    quat q;
    q = vec4(
        a.wwww * b 
      + (a.xyzx * b.wwwx + a.yzxy * b.zxyy) * vec4(1, 1, 1,-1) 
      - a.zxyz * b.yzxz
        );
    return q;
}

#define CH iChannel0
#define CHR iChannelResolution[0]

float maxHeight()
{
    return .3; //200. / CHR.x;        
}

float precis()
{
    return 1.5 / CHR.x;        
}

float terrain(vec2 p)
{
    p.x /= CHR.x / CHR.y;    
    p += .5;        
    if (clamp(p, 0., 1.) != p) return .5;    
    vec4 t = texture(CH, p);
    return t.r * maxHeight();
}

vec3 terrainGrad(vec3 p, out vec2 curv)
{    
    float prec = precis();
    float hC = terrain(p.xz);    
    vec3 pC = vec3(p.x, hC, p.z),
      pR = p + vec3(prec, 0,  0),
      pL = p - vec3(prec, 0,  0),
      pT = p + vec3( 0, 0, prec),
      pB = p - vec3( 0, 0, prec);    
    pR.y = terrain(pR.xz);
    pL.y = terrain(pL.xz);
    pT.y = terrain(pT.xz);
    pB.y = terrain(pB.xz);    
    vec3 n = normalize(cross(pT - pB, pR - pL));    
    float cx = dot(pC + pC - pR - pL, n),
          cy = dot(pC + pC - pT - pB, n);
    curv = vec2(cx, cy) / prec;
    return n;
}

// recombined
vec3 skybox(vec3 dir, bool blurry)
{
    float g = dir.y * .5 + .5;
    if (!blurry) {
        g *= g;
        g *= g;
    }
    g = 1.-g;
    g *= g * g;
    if (!blurry) {
        g *= g;
        g *= g;
    }
    g = 1.-g;
    if (!blurry) {
        g = smoothstep(0., 1., g);
        g = smoothstep(0., 1., g);
        g = smoothstep(0., 1., g);
        g = smoothstep(0., 1., g);
    }    
    vec3 h = pow(vec3(g), vec3(8, 1, 1));
    h = vec3(1.)-h;
    h = pow(h, vec3(.4, .5, 4));
    h = vec3(1.)-h;    
    return mix(vec3(.99,.99,.99), vec3(.01,.02,.2), h) * 2.;
}

vec3 render(vec3 camPos, vec3 camDir)
{
    vec3 sky = skybox(camDir, false); // we'll need it regardless
    if (camPos.y >= 0. && camDir.y >= 0.) 
        return sky; // early out

    const float range = .7;
    vec3 p = camPos, near = p, far = p + camDir * range;
    float maxD = 1.;
    bool hit = false;
    // prone to undermarching b/c distance scaling is so wack, plus it's noisy
    // perhaps should run another eikonal smoothing pass to compute actual SDF
    // or simply use the gamma trick for mip maximum
    float t = 0.;
    for (int iter = 384; --iter > 0 && t < range; ) {
        p = t * camDir + camPos;
        float h = (p.y - terrain(p.xz)) / maxHeight() / 18.;
        if (abs(h) < .002 * t) {
            hit = true; 
            break;
        }
        t += .7 * h; // / range;
    }
    if (!hit) return sky;
    maxD = t/range;

    vec2 curve2;
    vec3 normal = terrainGrad(p, curve2);

    vec2 posCurve2 =  max(vec2(0), curve2);
    vec2 negCurve2 = -min(vec2(0), curve2);

    float posCurve = posCurve2.x + posCurve2.y;
    posCurve = posCurve / (.2 + posCurve);
    float negCurve = negCurve2.x + negCurve2.y;
    negCurve = negCurve / (.2 + negCurve);
    float curve = (curve2.x + curve2.y);
    curve = curve / (.2 + abs(curve));

    p = vec3(p.x, terrain(p.xz), p.z);

    vec3 albedo  = vec3(1);

    float slopeFactor;
    float heightFactor;
    {
        heightFactor = p.y / maxHeight();

        heightFactor = smoothstep(0., 1., heightFactor);        
        heightFactor = smoothstep(0., 1., heightFactor);

        slopeFactor = abs(normal.y);
        slopeFactor *= slopeFactor;
        slopeFactor *= slopeFactor;
        slopeFactor *= slopeFactor;
        //slopeFactor *= slopeFactor;
        slopeFactor = 1.-slopeFactor;
        slopeFactor *= slopeFactor;
        slopeFactor *= slopeFactor;
        //slopeFactor *= slopeFactor;
        slopeFactor = 1.-slopeFactor;
        //slopeFactor = smoothstep(0., 1., slopeFactor);
        //slopeFactor = smoothstep(0., 1., slopeFactor);
        //slopeFactor = smoothstep(0., 1., slopeFactor);

        const vec3
            flatLow = vec3(.1, .4, .1),
            flatHigh = vec3(.7, .7, .5),        
            slopeLow = vec3(.4, .7, .2),
            slopeHigh = vec3(.3, .2, .1);

        vec3 flatColor = mix(flatLow, flatHigh, heightFactor);
        vec3 slopeColor = mix(slopeLow, slopeHigh, heightFactor);

        albedo = mix(slopeColor, flatColor, slopeFactor);
    }

    albedo = mix(albedo, (albedo+vec3(.7))*vec3(.7,.6,.3), posCurve); 

    float rivers;
    {
        rivers = max(0.,negCurve - posCurve);

        rivers = pow(rivers, pow(2., mix(.5, -.7, slopeFactor) + mix(-.7, .7, heightFactor)));   

        rivers = smoothstep(0., 1., rivers);
        rivers = smoothstep(0., 1., rivers);

        albedo = mix(albedo, vec3(.1,.2,.5), rivers); 
    }
    float snow = pow(heightFactor, 8.);
    albedo = min(mix(albedo, vec3(8), snow), 1.);

    albedo *= albedo; // guess all those colors were specified in sRGB gamma?

    const vec3 lightDir = normalize(vec3(0, 3, 5)),
      lightColor = 2. * vec3(1., .75, .5);    
    float nl = max(0., dot(normal, lightDir));

    float specm = 0.;
    specm = mix(specm, .5, posCurve);
    specm = mix(specm, 3., rivers);
    specm = mix(specm, 2., snow);

    float spec = max(0., dot(camDir, reflect(lightDir, normal))); // phong specular
    spec = pow(spec, 128.);

    float ambient = normal.y * .5 + .5; // hemisphere ambient
    ambient *= .2*exp2(-4. * negCurve);
    vec3 ambientColor = ambient * skybox(normal, true);

    vec3 color = nl*lightColor*(albedo + spec*specm) + ambientColor * albedo;

    float d2 = t*t; //dot(p - camPos, p - camPos);  
    color = mix(sky, color, exp2(-3.*d2)); // fog  
    return color;
}

