    fragCoord /= vec2(textureSize(tex, 0));
    fragCoord += vec2
    (
        RandomFloat01(rngState),
        RandomFloat01(rngState)
    );

    return textureLod(tex, fragCoord, 0.0) - textureLod(tex, fragCoord, 20.0);
}

