vec2 getCell(vec2 pos, float offset)
{
    pos = (pos);
    vec2 unit = vec2(0.,1.);
    float theta = sin(pos.x)-cos(pos.y)+offset;
    float rand1 = unit.x*cos(theta) + unit.y*sin(theta);
    float rand2 = unit.y*cos(theta) - unit.x*sin(theta);
    return vec2(rand1, rand2);
}

vec3 dVal(vec2 pos, float mixer, float offset)
{
    vec2 pos1 = (pos+offset/2.);
    vec2 cell1 = getCell(pos,offset);
    vec2 cell2 = getCell(vec2(pos.x+offset, pos.y),offset);
    vec2 cell3 = getCell(pos+offset,offset);
    vec2 cell4 = getCell(vec2(pos.x, pos.y+offset),offset);
    float dist1 = length(vec2(cell1.y,-cell2.x));
    float dist2 = length(vec2(cell1.y,-cell3.x));
