}

float f_n_noise_smooth_layered(
    vec2 o_pix_coo_nor,
    float n_freq_start, 
    float n_layers
){
    float n_freq = n_freq_start;
    float n_noise_smooth = f_n_noise_smooth(o_pix_coo_nor*n_freq);

    float n_amp = 0.5; 
    float n_amp_sum = 0.0;
    for(
        float n_layer_nor = 0.0;
        n_layer_nor < 1.0;
        n_layer_nor += (1.0/n_layers)
    ){
        n_freq *= 2.0;
        n_noise_smooth += f_n_noise_smooth(o_pix_coo_nor*n_freq)*n_amp;
        n_amp_sum += n_amp;
        n_amp*=0.5;
    }

    n_noise_smooth /= 1.0+n_amp_sum;

    return n_noise_smooth;
}

