////////////////////////////////////////////////////////////////////////////////
//
// Progressive Dragon Curve. mla, 2022.
//
// Uses an explicit stack to keep track of the recursion.
// This one keeps track of intermediate segments and avoids recursing far from
// the point being plotted.
//
// <mouse>: show recursion for mouse point
// 'a': show shortcut lines
// 'x': show entire curve
//
////////////////////////////////////////////////////////////////////////////////

const int maxdepth = 10; // How deeply to recurse

// Dragon Curve L-system
#define C(turns,offset) (((turns) << 16) | ((offset) & 0xffff)) // Encoding
const int A = 0, B = 3, END = -1, L = -1, R = 1;
const int program[6] = int[] (C(0,A),C(R,B),C(0,END),C(0,A),C(L,B),C(0,END));
const float factor = 2.0; // (Area) scale factor

// Global state
vec2 point = vec2(0);         // The drawing point
vec2 movingpoint = vec2(0); // The moving point
bool foundmovingpoint = false;
float pathlen = 0.0;          // Cumulative path length

// Calculate and draw segment in final level
float drawsegment(vec2 pos, float seglen, float theta, float t) {
  assert(t > pathlen);
  vec2 seg = vec2(cos(theta),sin(theta));
  if (t >= pathlen && t < pathlen+seglen) {
    foundmovingpoint = true;
    movingpoint = point + (t-pathlen)*seg;
    seglen = t-pathlen;
  }
  vec2 newpoint = point + seglen*seg;
  float d = 1e8;
  d = segment(pos,point,newpoint);
  //polywind(pos,point,newpoint);
  pathlen += seglen;
  point = newpoint;
  return d;
}

vec2 map(vec2 pos) {
  pos = (2.0*pos - iResolution.xy)/iResolution.y;
  pos *= 1.666;
  pos = -pos;
  pos -= vec2(-0.8,0.71);
  return pos;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 pos = map(fragCoord);
  vec2 pos2 = pos;
  if (iMouse.z > 0.0) pos2 = map(iMouse.xy);
  int stack[maxdepth];
  float totalpath = 4.0*pow(factor,0.5*float(maxdepth+1));
  float t = mod(2.0*iTime,totalpath+10.0); // Moving point position
  t = min(t,totalpath);
  if (key(CHAR_X)) t = totalpath;
  float angledelta = 0.0;
  if (!key(CHAR_Z)) angledelta = 0.015;
  float angle = PI/2.0+angledelta;
  float magic = 0.5*angle; // Segment rotation at each recursion level
  float d = 1e8, d1 = 1e8; // Line distances
  float theta = -4.0*angledelta;//0.0;       // Cumulative angle
  float tt = 0.0;
  // Draw four curves, which just happen to join up continuously.
  for (int i = 0; i < 4; i++,theta+=angledelta){
    int depth = 0;
    stack[depth++] = B;
    float seglen = 1.0;
    while (depth > 0 && pathlen < t) {
      int instr = program[stack[depth-1]++];
      // Decode instruction
      int turns = instr>>16;
      int code = instr<<16>>16;
      theta += float(turns)*angle; // Apply turn
      if (code == END) {
        depth--;    // Terminate level & unwind
        seglen *= sqrt(factor);
      } else if (depth == maxdepth) {
        float d1 = drawsegment(pos,seglen,theta,t); // Segment
        if (d1 < d) { d = d1; tt = pathlen; }
      } else {
        // Compute the end point of the segment at the current level
        float depthdelta = float(maxdepth-depth);
        float theta1 = theta + magic*(depthdelta-1.0);
        // Adjust for segment type
        if (code == A) theta1 += magic;
        else theta1 -= magic;
        float seglenfactor = pow(sqrt(1.0+cos(angle)),depthdelta);
        vec2 newpoint = point+seglenfactor*seglen*vec2(cos(theta1),sin(theta1));
        d1 = min(d1,segment(pos,point,newpoint));
        if (distance(pos2,0.5*(point+newpoint)) > seglen) {
          // Modify state as if we had done the full recursion
          //polywind(pos,point,newpoint);
          point = newpoint;
          pathlen += seglen*pow(factor,0.5*depthdelta);
          // Each non-final segment changes theta by angle.
          if (code == A) theta += angle;
          else theta -= angle;
        } else {
          // Actually recurse
          stack[depth++] = code;
          seglen /= sqrt(factor);
        }
      }
    }
  }
  //polywind(pos,point,vec2(0)); // Just in case rounding error prevents exact closure...
  float px = fwidth(pos.x);
  vec3 col = texture(iChannel0,0.2*pos).xyz; //vec3(0);//vec3(1,1,0.8);
  col = 0.1*pow(col,vec3(2.2));
  vec3 pcol = h2rgb(tt/totalpath);
  //if (!key(CHAR_F) && (windnum&1) != 0) col = vec3(0,0,0.05);
  if (key(CHAR_A) || iMouse.z > 0.0) col = mix(vec3(0.5), col,smoothstep(0.0,px,d1));
  col = mix(pcol, col,smoothstep(0.0,px,d));
  if (foundmovingpoint) {
    col = mix(vec3(1), col,smoothstep(0.0,px,distance(pos,movingpoint)-0.01));
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

