//hashing noise by IQ
float hash( int k ) {
    uint n = uint(k);
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.0;
}

// Integer Hash - II by iq
// https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C

vec3 hash3( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

vec2 world2screenInternal(in vec2 p, in vec2 resolution)
{
    return (p + 1.0) * 0.5 * resolution;
}

#define world2screen(X) world2screenInternal(X, iResolution.xy)

vec2 screen2worldInternal(in vec2 p, in vec2 resolution)
{
    return (p / resolution) * 2.0 - 1.0;
}

#define screen2world(X) screen2worldInternal(X, iResolution.xy)

const float PI = 3.141592653589793;

mat2 rot2(float a)
{
    vec2 sc = vec2(sin(a), cos(a));
    return mat2(sc.y, sc.x, -sc.x, sc.y);
}

void fxCalcCamera(in float time, in vec2 mouse, out vec3 cameraLookAt, out vec3 cameraPos, out vec3 cameraFwd, out vec3 cameraLeft, out vec3 cameraUp)
{
    cameraLookAt = vec3(0,-.02,0);
    vec3 delta = vec3(.2,sin(time*.1)*0.02 + 0.06 + mouse.y * 0.1,.2) * (.8 - mouse.y * .4);
    delta.xz *= rot2(time * .05 + mouse.x * 4.);
    cameraPos = cameraLookAt + delta;

    cameraFwd  = normalize(cameraLookAt - cameraPos);
    cameraLeft = -normalize(cross(cameraFwd, vec3(0.0,1.0,0.0)));
    cameraUp   = normalize(cross(cameraLeft, cameraFwd)) * 0.5;
}

vec3 fxCalcRay(in vec2 fragCoord, in vec3 iResolution, in vec3 cameraFwd, in vec3 cameraUp, in vec3 cameraLeft)
{
	vec2 screenPos = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;
	return normalize(cameraFwd - screenPos.x * cameraLeft - screenPos.y * cameraUp);
}

float length2(vec2 v)
{
    return dot(v, v);
}

float fxLinePointDist2(vec2 a, vec2 b, vec2 p)
{
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);// proj coord on line
    return length2(p - b * h); // squared dist to segment
}

float distanceFromWalls(vec2 point, vec3 resolution, float time)
{
#if 1
    return 1. - length(point);
#else
    float minDist = 1e30;
    minDist = min(minDist, point.x + resolution.x / resolution.y);
    minDist = min(minDist, resolution.x / resolution.y - point.x);
    minDist = min(minDist, point.y + 1.0);
    minDist = min(minDist, 1.0 - point.y);
    return minDist;
#endif
}

vec2 getNormalFromWalls( vec2 point, vec3 resolution, float time )
{
	vec2 tinyChangeX = vec2( 0.001, 0.0 );
    vec2 tinyChangeY = vec2( 0.0 , 0.001 );

   	float upTinyChangeInX   = distanceFromWalls( point + tinyChangeX, resolution, time ); 
    float downTinyChangeInX = distanceFromWalls( point - tinyChangeX, resolution, time ); 

    float tinyChangeInX = upTinyChangeInX - downTinyChangeInX;


    float upTinyChangeInY   = distanceFromWalls( point + tinyChangeY, resolution, time ); 
    float downTinyChangeInY = distanceFromWalls( point - tinyChangeY, resolution, time ); 

    float tinyChangeInY = upTinyChangeInY - downTinyChangeInY;


	vec2 normal = vec2(
         			tinyChangeInX,
        			tinyChangeInY
    	 		  );

	return normalize(normal);
}

