struct Demo
{
    float used;      // what pct of the total particle buffer should be used
    float size;      // how big should the particle be
    float speed;     // how fast should the particles travel
    vec4 turn;       // how fast can the particles turn to follow a trail
    vec4 spin;       // how fast do the particles turn on average (signed)
    int colors;      // how many colors does this demo use
    vec4 colorR;     // color for red particles (specular in alpha)
    vec4 colorG;     // color for green particles
    vec4 colorB;     // color for blue particles
    vec4 colorA;     // color for alpha particles 
    vec4 background; // background color
    float checker;   // should the floor be checkered
};

const int NUM_DEMOS = 12;
const Demo DEMOS[NUM_DEMOS] = Demo[](

    Demo(.9,   .001,  .006, vec4(.6),          vec4(.09),             2, vec4(.6,.05,.1,.2), vec4(.8,.1,.1,.2),   vec4(0),             vec4(0),            vec4(.8,.6,.4,0),    0.), // sand dunes
    Demo(.05,  .0005, .001, vec4(.9),          vec4(0.),              4, vec4(0,0,0,1),      vec4(.1),            vec4(.2),            vec4(.3),           vec4(1,.3,.1,0),     0.), // lava
    Demo(.05,  .0002, .001, vec4(.3,.3,.3,.0), vec4(-.01,.01,.02,1.), 4, vec4(1),            vec4(.9),            vec4(.8),            vec4(.8,.4,.2,0),   vec4(.4,.4,1,1),     0.), // islands
    Demo(.9,   .0003, .001, vec4(.7),          vec4(0.),              1, vec4(1,0,1,1),      vec4(1,0,1,0),       vec4(1,0,1,0),       vec4(1,0,1,0),      vec4(.4,1,.4,1),     1.), // toxic snakes
    Demo(.2,   .0004, .003, vec4(.3),          vec4(0.),              1, vec4(.2,.2,.2,1),   vec4(.2),            vec4(.2),            vec4(.2),           vec4(.9,.9,.8,0),    0.), // ink
    Demo(.01,  .001,  .002, vec4(.3,.1,0,0),   vec4(0.),              2, vec4(.5,.2,.2,0),   vec4(.1,.5,.3,0),    vec4(.8),            vec4(.7),           vec4(.6,.9,.6,0),    0.), // redwood
    Demo(.05,  .0002, .002, vec4(.3),          vec4(.05),             1, vec4(0,0,1,1),      vec4(0,0,1,0),       vec4(0,0,1,0),       vec4(0,0,1,0),      vec4(1,0,0,0),       0.), // red blue
    Demo(.05,  .002,  .002, vec4(1,.7,.5,1),   vec4(.1,-.1,0,0),      4, vec4(1,1,0,0),      vec4(1, .7, 0,0),    vec4(1,.5,0,0),      vec4(.2,.4,.2,0),   vec4(.2),            0.), // red yellow
    Demo(.5,   .0004, .004, vec4(1.5),         vec4(-.03),            2, vec4(0,0,1,1),      vec4(1,0,0,1),       vec4(1,0,0,0),       vec4(0,0,1,0),      vec4(1),             1.), // red blue
    Demo(.1,   .0002, .002, vec4(1),           vec4(0,0,1,0),         3, vec4(.1,.5,.1,.3),  vec4(.2,.6,.7,1),    vec4(.4),            vec4(.4),           vec4(.5,.4,.1,0),    0.), // rock garden
    Demo(.6,   .0001, .002, vec4(1),           vec4(0),               4, vec4(.22,.4,.4,0),  vec4(.58,.48,.32,0), vec4(.85,.85,.82,0), vec4(.2,.22,.24,0), vec4(.85,.77,.67,0), 0.), // pollock
    Demo(.01,  .001,  .001, vec4(.3),          vec4(0),               1, vec4(.5,1.4,0,1),   vec4(0),             vec4(0),             vec4(0),            vec4(0),             0.)  // glow 'n the dark worms
);

Demo g_Demo;
int g_MaxParticles = 10000; 

void initGlobals(vec3 res, vec4 state)
{
    Demo demo = DEMOS[int(state.w) % NUM_DEMOS];
    Demo nextDemo = DEMOS[int(state.w + 1.) % NUM_DEMOS];

    float used = fract(state.w) > .9 ? min(demo.used, nextDemo.used) : demo.used;
    g_MaxParticles = int(res.x * res.y * used) / NUM_PARTICLE_DATA_TYPES;

    float transition = smoothstep(.9, 1., fract(state.w));
    g_Demo.colors = demo.colors;
    g_Demo.size = mix(demo.size, nextDemo.size, transition)*.02;
    g_Demo.speed = mix(demo.speed, nextDemo.speed, transition);
    g_Demo.turn = mix(demo.turn, nextDemo.turn, transition);
    g_Demo.spin = mix(demo.spin, nextDemo.spin, transition);
    g_Demo.colorR = mix(demo.colorR, nextDemo.colorR, transition);
    g_Demo.colorG = mix(demo.colorG, nextDemo.colorG, transition);
    g_Demo.colorB = mix(demo.colorB, nextDemo.colorB, transition);
    g_Demo.colorA = mix(demo.colorA, nextDemo.colorA, transition);
    g_Demo.background = mix(demo.background, nextDemo.background, transition);
    g_Demo.checker = mix(demo.checker, nextDemo.checker, transition);
}

vec4 colorFromId(int id)
{
    int index = id % g_Demo.colors;

    vec4 result = vec4(0);
    result[index] = 1.;

    return result;
}

float sdCylinder( vec3 p, vec3 c )
{
    return length(p.xz-c.xy)-c.z;
}
const float MAX_Y = 0.003;

float scene(sampler2D sampler, vec3 res, vec3 p)
{
    vec4 colors = min(texture(sampler, p.xz * vec2(1, res.x / res.y) + .5) * .012, vec4(MAX_Y));
    float h = max(max(max(colors.x, colors.y), colors.z), colors.a);
    float height = p.y - h;

    float b = sdCylinder(p, vec3(0,0,.25));
    return max(b, height);
}

// https://iquilezles.org/articles/normalsSDF
vec3 grad( sampler2D sampler, vec3 res, in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*scene(sampler, res, pos + e.xyy*eps ) + 
					  e.yyx*scene(sampler, res, pos + e.yyx*eps ) + 
					  e.yxy*scene(sampler, res, pos + e.yxy*eps ) + 
					  e.xxx*scene(sampler, res, pos + e.xxx*eps ) );
}

float g_MaxT = 0.4;
const float SDF_EPSILON = 0.00005;
vec3 rayMarch(sampler2D sampler, vec3 res, vec3 pos, vec3 dir, out float t)
{
    t = 0.;

    if (pos.y > MAX_Y) // skip immediately down to the max Y so we don't do unnecessary texture fetches
    {
        if (dir.y > 0.) { t = g_MaxT; return vec3(1e6); }
        t = (MAX_Y - pos.y) / dir.y;
        pos += dir * t;
    }

    float g = 1., s = .5;
    for (int i = 0; i < 100; ++i)
    {
        float d = scene(sampler, res, pos);

        if (abs(d) < SDF_EPSILON || t > g_MaxT)
        {
            break;
        }

        if (g != sign(d))
        {
            g = -g;
            //s *= .5;
        }

        t += d * s;
        pos += dir * d * s;
    }

    return pos;
}

// From iq's  Filtered checker (triangle, 3D)
// https://www.shadertoy.com/view/llffWs

// --- analytically triangle-filtered checkerboard ---
vec3 pri( in vec3 x )
{
    // see https://www.shadertoy.com/view/MtffWs
    vec3 h = fract(x/2.0)-0.5;
    return x*0.5 + h*(1.0-2.0*abs(h));
}

float checkersTextureGradTri( in vec3 p, in vec3 ddx, in vec3 ddy )
{
    vec3 w = max(abs(ddx), abs(ddy)) + 0.01;       // filter kernel
    vec3 i = (pri(p+w)-2.0*pri(p)+pri(p-w))/(w*w); // analytical integral (box filter)
    return 0.5 - 0.5*i.x*i.y*i.z;                  // xor pattern
}

vec4 pixelInternal(sampler2D sampler, vec3 res, vec3 cameraPos, vec3 cameraFwd, vec3 rayDir, out vec3 hitPos, out vec3 normal)
{
    g_MaxT = .10 + length(cameraPos.xz);

    float t;
    hitPos = rayMarch(sampler, res, cameraPos, rayDir, t);

    normal = grad(sampler, res, hitPos);

    // Compute color
    vec4 background = g_Demo.background;
    float checker = 0.;
    if (g_Demo.checker > 0.)
    {
#if ADAPTIVE_AA // Don't use analytic AA and adaptive AA at the same time...they don't get along
        vec2 m = mod(hitPos.xz, .02);
		checker = float((m.x < .01) ^^ (m.y < .01));
#else
		vec3 uvw = hitPos.xzy * 100.;

		// calc texture sampling footprint		
		vec3 ddx_uvw = dFdx( uvw ); 
        vec3 ddy_uvw = dFdy( uvw ); 

		// shading		
        checker = checkersTextureGradTri(uvw, ddx_uvw, ddy_uvw);
#endif
    }

    background = 1.5*mix(g_Demo.background, g_Demo.background * checker, g_Demo.checker);
    vec4 colors = textureLod(sampler, (hitPos.xz * vec2(1, res.x / res.y) + .5), 3.);

    // Pretty much want the max, but we want to blend if the next-biggest color(s) are close
    float cMax = max(max(max(colors.r, colors.g), colors.b), colors.a);
    vec4 rdcd = max(vec4(0), colors - cMax + .01); // reduce all weights by the max with small offset
    vec4 color = (rdcd.r * g_Demo.colorR + // weighted sum of reduced weights
                  rdcd.g * g_Demo.colorG +
                  rdcd.b * g_Demo.colorB +
                  rdcd.a * g_Demo.colorA)
        / (rdcd.r + rdcd.g + rdcd.b + rdcd.a);

    color = mix(background, color, smoothstep(0., .02, cMax));

    // Apply light and shadow
    const vec3 LIGHT_DIR = normalize(vec3(1, .2, 1));
    const float AMBIENT = 0.3;

#if SHADOW
    float shadowT = g_MaxT;    
    vec3 offsetPos = hitPos + normal * SDF_EPSILON * 2.;
    rayMarch(sampler, res, offsetPos, LIGHT_DIR, shadowT);

    if (shadowT < g_MaxT)
    {
        color = color * AMBIENT;
    }
    else
#endif // SHADOW
    {
        float dp = max(0., dot(normal, LIGHT_DIR));
        color = color * (dp * (1. - AMBIENT) + AMBIENT);

        vec3 reflection = reflect(LIGHT_DIR, normal);
        dp = dot(cameraFwd, reflection);

        color.rgb += color.a * pow(abs(dp), 15.0) * vec3(0.5);
    }

    // Fade at distance
    if (t >= g_MaxT*.75)
    {
        vec4 sky = g_Demo.background * (.5 - 2. * rayDir.y);
        color = mix(color, sky, smoothstep(g_MaxT*.75, g_MaxT, t));
    }

    return vec4(color.xyz, t);
}

#define pixel(A, B, C, D, E) pixelInternal(iChannel0, iResolution, A, B, C, D, E)

