    //https://www.youtube.com/watch?v=BFld4EBO2RE&list=RDCMUCdmAhiG8HQDlz8uyekw4ENw&in
    float r = 1.+0.15*pow(.5+.5*sin(6.28*iTime+st.y/25.0),4.);
    vec3 xyz = vec3(st.x,st.y,sqrt(r*r-st.x*st.x-st.y*st.y));
    xyz.z = xyz.z*(2.-xyz.y/15.);
    xyz.y = 1.2*xyz.y-abs(xyz.x)*sqrt((20.*abs(xyz.x)/15.));


    vec3 color = vec3(0.);

    //Ray light
    vec3 ro = vec3(0.);
    vec3 rd = normalize(vec3(xyz.x,xyz.y,xyz.z));

    //Sphere
    vec3 SpCenter = vec3(0.,0.,8.);


    float map = dot(SpCenter-ro,rd);
    vec3 p = ro + map*rd;

