bounds worldBounds() 
{
    bounds b;
    b.mini = vec3
    (
        0,
        0,
        0
    );

    b.maxi = vec3
    (
        iChannelResolution[0].x,
        600,
        iChannelResolution[0].y
    );
    return b;
}

vec3 boundsToWorld(vec3 boundsPoint)
{
    bounds b = worldBounds();
    return mix
    (
        b.mini,
        b.maxi,
        boundsPoint
    );
}

vec3 worldToBounds(vec3 worldPoint)
{
    bounds b = worldBounds();
    return invMix
    (
        b.mini,
        b.maxi,
        worldPoint
    );
}

vec3 projectBoundsPoint(vec3 p, bool zeroEdge)
{
    if(zeroEdge && clamp(p,0.0,1.0) != p) 
    {
        p.y = 0.0;
        return p;
    }

    vec4 t = texture(iChannel0, p.xz);
    p.y = t.b / t.a;
    return p;
}

vec3 projectWorldPoint(vec3 p, bool zeroEdge)
{
    p = worldToBounds(p);
    p = projectBoundsPoint(p, zeroEdge);
    p = boundsToWorld(p);
    return p;
}

bool isWorldPointUnderTerrain(vec3 p)
{
