bool raycast(vec3 camPos, vec3 camDir, uint rngState, vec2 fragCoord, out vec3 pos)
{
    bounds b = worldBounds();   
    pos = vec3(0);

    vec3 near, far;
    if(!rayIntersectBounds(b, camPos, camDir, near, far))
    {
        return false;
    }

    pos = near;
    float minD = 0.0, maxD = 1.0;
    bool hit = false;
    const float increment = 0.005;

    #ifdef NO_DITHER
    const float start = 0.0;
    #else
    // Randomize ray starting point to fuzz undersampled edges.
    float noise = sampleBlueNoise(iChannel2, fragCoord, rngState).x;
    float start = increment * noise;
    #endif

    for(float i = start;; i += increment)
    {
        float j = i;
        if(j > 1.0) j = 1.0;
        j = i*i;

    	pos = mix(near, far, j);

        maxD = j;

        if(isWorldPointUnderTerrain(pos))
        {
            hit = true;
            break;
        }

        minD = j;

        if(i > 1.0) 
        {
            break;
        }
    }

    if(!hit) return false;

    maxD = (minD + maxD) * 0.5;
    float stepSize = (maxD - minD) * 0.5;

    for(int i = 0; i < 5; i++, stepSize *= 0.5)
    {
    	pos = mix(near, far, maxD);
        if(!isWorldPointUnderTerrain(pos))
        {
            minD = maxD;
            maxD += stepSize;
        }
        else
        {
            maxD -= stepSize;
        }
    }

    return true;
}

vec4 skybox(vec3 dir)
{
    float gradient = dir.y * 0.5 + 0.5;

    gradient *= gradient;
    gradient *= gradient;

    gradient = 1.-gradient;
    gradient *= gradient;
    gradient *= gradient;
    gradient *= gradient;
    gradient *= gradient;
    gradient = 1.-gradient;

    gradient = smoothstep(0., 1., gradient);
    gradient = smoothstep(0., 1., gradient);
    gradient = smoothstep(0., 1., gradient);
    gradient = smoothstep(0., 1., gradient);

    vec3 gradient3 = pow(vec3(gradient), vec3(8.0, 1.0, 1.0));
    gradient3 = vec3(1.)-gradient3;
    gradient3 = pow(gradient3, vec3(0.4, 0.5, 4.0));
    gradient3 = vec3(1.)-gradient3;

    vec3 color = mix(vec3(0.99,0.99,0.99), vec3(0.01,0.02,0.2), gradient3) * 2.0;

    return vec4(sqrt(color), 1.0);
}

vec3 envLighting(vec3 dir)
{
    float gradient = dir.y * 0.5 + 0.5;

    gradient = 1.-gradient;
    gradient *= gradient * gradient;
    gradient = 1.-gradient;


    vec3 gradient3 = pow(vec3(gradient), vec3(8.0, 1.0, 1.0));
    gradient3 = vec3(1.)-gradient3;
    gradient3 = pow(gradient3, vec3(0.4, 0.5, 4.0));
    gradient3 = vec3(1.)-gradient3;

    vec3 color = mix(vec3(0.99,0.99,0.99), vec3(0.01,0.02,0.2), gradient3) * 5.0;

    return sqrt(color);
}

vec4 shade(bool hit, vec3 hitPoint, vec3 camPos, vec3 camRay)
{
    if(!hit) return vec4(0);

    vec3 normal;
    vec2 curvature;
    worldPointToNormalCurvature(hitPoint, normal, curvature);


    vec2 posCurve2 = max(vec2(0), curvature);
    vec2 negCurve2 = -min(vec2(0), curvature);

    float posCurve = posCurve2.x + posCurve2.y;
    posCurve = posCurve / (0.025 + posCurve);
    float negCurve = negCurve2.x + negCurve2.y;
    negCurve = negCurve / (0.075 + negCurve);
    float curve = (curvature.x + curvature.y);
    curve = curve / (0.075 + abs(curve));

    //posCurve = negCurve = curve = 0.;

    //hitPoint /= 10.;
    //hitPoint -= floor(hitPoint);

    vec3 reflDir = reflect(camRay, normal); 

    Indirect ind;
    {
        ind.diffuse = envLighting(normal);
        ind.specular = skybox(reflDir).rgb;

        vec3 ambientDir = vec3(0, 1, 0);
        {
            float ambient = dot(normal, ambientDir) * 0.5 + 0.5;
            ambient *= ambient;
            ambient *= ambient;
            //ambient *= ambient;
            ambient *= curve * 0.5 + 0.5;
            ind.diffuse *= ambient; 
        }
        //return vec4(ind.diffuse, 1.0);
        {
            float ambient = dot(normal, ambientDir) * 0.5 + 0.5;
            ambient *= ambient;
            ambient *= ambient;
            //ambient *= ambient;
            ambient *= curve * 0.5 + 0.5;
            ind.specular *= ambient; 
        }
        //return vec4(ind.specular, 1.0);
    }


    Light light;
    light.dir = normalize(vec3(0, 3, 5));
    light.color = vec3(2.0, 1.5, 1.0);

    MaterialMetallic mat;
    {
        mat.albedo  = vec3(1.);

        float slopeFactor;
        float heightFactor;
        {
            heightFactor = hitPoint.y / worldBounds().maxi.y;

            heightFactor = smoothstep(0., 1., heightFactor);        
            heightFactor = smoothstep(0., 1., heightFactor);

            slopeFactor = abs(normal.y);
            slopeFactor *= slopeFactor;
            slopeFactor *= slopeFactor;
            slopeFactor *= slopeFactor;
            //slopeFactor *= slopeFactor;
            slopeFactor = 1.-slopeFactor;
            slopeFactor *= slopeFactor;
            slopeFactor *= slopeFactor;
            //slopeFactor *= slopeFactor;
            slopeFactor = 1.-slopeFactor;
            //slopeFactor = smoothstep(0., 1., slopeFactor);
            //slopeFactor = smoothstep(0., 1., slopeFactor);
            //slopeFactor = smoothstep(0., 1., slopeFactor);

            vec3 flatLow = vec3(0.1, 0.4, 0.05);
            vec3 flatHigh = vec3(0.7, 0.7, 0.5);

            vec3 slopeLow = vec3(0.4, 0.7, 0.2);
            vec3 slopeHigh = vec3(0.3, 0.2, 0.05);

            vec3 flatColor = mix(flatLow, flatHigh, heightFactor);
            vec3 slopeColor = mix(slopeLow, slopeHigh, heightFactor);

            mat.albedo = mix(slopeColor, flatColor, slopeFactor);
        }

        // Add texture.
        {
            vec2 texUV = (hitPoint.xz * 8.0) / iChannelResolution[3].xy;

            mat.albedo *= texture(iChannel3, texUV).rgb;
            mat.albedo /= texture(iChannel3, texUV, 16.).rgb;
        }

        float crestFactor = slopeFactor;
        // Highlight cracks.
        {
            crestFactor *= crestFactor;
            crestFactor *= crestFactor;
            crestFactor = 1.-crestFactor;
            crestFactor = crestFactor * 0.6 + 0.4;
            float crests = posCurve;
            crests = 1.-crests;
            crests = lerp(crests, crests*crests, 0.5);
            crests = 1.-crests;
            crestFactor *= crests;
            mat.albedo = mix(mat.albedo, (mat.albedo*1.0+vec3(0.75))*vec3(0.7,0.65,0.3), crestFactor); 
        }

        // Add rivers.
        float rivers;
        {
            rivers = max(0.,negCurve - posCurve);

            rivers = pow(rivers, pow(2., mix(0.5, -0.7, slopeFactor) + mix(-0.7, 0.7, heightFactor)));   

            //rivers = pow(rivers, heightFactor + 1.0);   
            //rivers = pow(rivers, heightFactor*3.+0.5);         

            rivers = smoothstep(0., 1., rivers);
            rivers = smoothstep(0., 1., rivers);

            //rivers = 1.-rivers;
            //rivers *= rivers;
            //rivers = 1.-rivers;

            mat.albedo = mix(mat.albedo, vec3(0.1,0.2,0.5), rivers); 
        }



        //mat.albedo *= mat.albedo * 2.0;
        //mat.albedo = vec3(1.);

        mat.smoothness = 0.0;
        mat.smoothness = mix(mat.smoothness, 1., 0.75 * crestFactor);//length(color)*length(color)*0.2;
        mat.smoothness = mix(mat.smoothness, 1., 1.0 * rivers);

        mat.metallic = crestFactor;
        mat.metallic *= mat.metallic;
    }

    //MaterialMetallic mat, vec3 normal, vec3 viewDir, Light light, Indirect indirect
    vec3 color = lighting(mat, normal, -camRay, light, ind);

    float fogScale = 1.0 / 1200.0;
    float fogDist = lengthSqr((hitPoint - camPos) * fogScale);

    color.rgb = mix(skybox(camRay).rgb, color.rgb, pow(vec3(0.5), fogDist * vec3(0.1,0.4,2.))); 

    return vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint rngState = uint
    (
        uint(iFrame) * uint(1973) 
    ) | uint(1);

    vec3 pivotPos   = boundsToWorld(vec3(0.5, 0.25, 0.5));
    vec3 camPos     = boundsToWorld(vec3(0.5, 1, 0.0));
    vec3 camOffset  = camPos - pivotPos;

    quaternion pan  = FromAngleAxis(vec3(0, iTime * 0.125, 0));
