    //camOffset       = mul(pan, camOffset);
    //camRay          = mul(mul(pan, tilt), camRay);

    vec3 normal; vec2 curvature;
    worldPointToNormalCurvature(camPos, normal, curvature);
    vec3 lookFwd;
    vec3 lookUp;
    vec3 lookRight;

    vec3 lookAtPoint = camPos + normalize(pivotPos - camPos) * 250.0;
    lookAtPoint          = projectWorldPoint(lookAtPoint, false) + vec3(0.0, 10.0, 0.0);

    lookFwd = normalize(lookAtPoint - camPos);
    lookUp = vec3(0,1,0);
    /*lookRight = safeNormalize(cross(lookUp, lookFwd));
    lookUp = normal;
    lookFwd = safeNormalize(cross(lookRight, lookUp)) + lookFwd * 5.0;
    lookUp = vec3(0,1,0);*/


    quaternion look = LookRotation(lookFwd, lookUp);
    vec3 camRay     = normalize(vec3((fragCoord - iResolution.xy * 0.5) / iResolution.yy, 1));

    camRay          = mul(look, camRay);

    fragColor = skybox(camRay);

    vec3 hitPoint;
    bool hit = raycast(camPos, camRay, rngState, fragCoord, hitPoint);
    vec4 foreground = shade(hit, hitPoint, camPos, camRay);

    blend(foreground, fragColor);

    // Vignette
    {
        half2 diff = abs(fragCoord / iResolution.xy - vec2(0.5));
        half2 maxDiff = vec2(0.5);
        vec4 _Vignette = vec4(1.0, 0.8, 2.0, 1.0); 

        half maxDistanceToCenter = pow( pow(maxDiff.x, _Vignette.z) + pow(maxDiff.y, _Vignette.z), 1.0/_Vignette.z);

        half distanceToCenter = pow( pow(diff.x, _Vignette.z) + pow(diff.y, _Vignette.z), 1.0/_Vignette.z) / maxDistanceToCenter;
        half vignette = saturate(distanceToCenter);
        vignette = pow(vignette, _Vignette.x);
        vignette = 1.0 - vignette;
        vignette *= _Vignette.w;
        vignette /= 1.0 + vignette;
        vignette /= _Vignette.w / (1.0 + _Vignette.w);
        vignette = smoothstep(1.0,0.0,vignette);
        vignette = lerp(0.0, vignette, _Vignette.y);
        fragColor.rgb = lerp(fragColor.rgb * vec3(2.0, 1.0, 0.5), vec3(0), pow(vec3(vignette), vec3(0.75, 2.0, 5.0)));

    }

    fragColor.rgb = fragColor.rgb / sqrt(1.0+fragColor.rgb*fragColor.rgb);
    fragColor.rgb = linearToGamma(fragColor.rgb);
    fragColor.rgb = smoothstep(vec3(0), vec3(1), fragColor.rgb);
}

