bounds worldBounds() 
{
    bounds b;
    b.mini = vec3
    (
        0,
        0,
        0
    );

    b.maxi = vec3
    (
        iChannelResolution[0].x,
        600,
        iChannelResolution[0].y
    );
    return b;
}

vec3 boundsToWorld(vec3 boundsPoint)
{
    bounds b = worldBounds();
    return mix
    (
        b.mini,
        b.maxi,
        boundsPoint
    );
}

vec3 worldToBounds(vec3 worldPoint)
{
    bounds b = worldBounds();
    return invMix
    (
        b.mini,
        b.maxi,
        worldPoint
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uvC = fragCoord.xy / iResolution.xy;
	vec2 uvR = (fragCoord.xy + vec2(1.25,0)) / iResolution.xy;
	vec2 uvT = (fragCoord.xy + vec2(0,1.25)) / iResolution.xy;
	vec2 uvL = (fragCoord.xy - vec2(1.25,0)) / iResolution.xy;
	vec2 uvB = (fragCoord.xy - vec2(0,1.25)) / iResolution.xy;
    vec2 colC = texture(iChannel0, uvC).ba;
    vec2 colR = texture(iChannel0, uvR).ba;
    vec2 colT = texture(iChannel0, uvT).ba;
    vec2 colL = texture(iChannel0, uvL).ba;
    vec2 colB = texture(iChannel0, uvB).ba;

    colC.r *= 1. / colC.g;
    colR.r *= 1. / colR.g;
    colT.r *= 1. / colT.g;
    colL.r *= 1. / colR.g;
    colB.r *= 1. / colT.g;


    vec3 posC = vec3(uvC.x, colC.r, uvC.y);
    vec3 posR = vec3(uvR.x, colR.r, uvR.y);
    vec3 posL = vec3(uvL.x, colL.r, uvL.y);
    vec3 posT = vec3(uvT.x, colT.r, uvT.y);
    vec3 posB = vec3(uvT.x, colB.r, uvB.y);

    posC = boundsToWorld(posC);
    posR = boundsToWorld(posR);
    posL = boundsToWorld(posL);
    posT = boundsToWorld(posT);
    posB = boundsToWorld(posB);

    vec3 dx = posR - posL;
    vec3 dy = posT - posB;

    vec3 n = -normalize(cross(dx, dy));

    float curveX = dot(posC + posC - posR - posL, n);
    float curveY = dot(posC + posC - posT - posB, n);

    vec2 c = vec2(curveX, curveY) / vec2(distance(posR, posL), distance(posT, posB));

    fragColor = vec4(n.xz, c);
}

