    float height01 = perlin(uv01, rngState);
    float height10 = perlin(uv10, rngState);
    float height11 = perlin(uv11, rngState);

     //height00 = 0.;
     //height01 = 0.;
     //height10 = 0.;
     //height11 = 0.;

    float height0X = mix(height00, height01, uvTile.x);
    float height1X = mix(height10, height11, uvTile.x);
