    // Increase contrast
    // (though this will flatten the tops and bottoms).
    v = v * 2.0 - 1.0;
    v = min(1.0, max(-1.0, v * 2.25));
    v = v * 0.5 + 0.5;

    // Make peaks pointer and valleys flatter.
    v *= v * v * v;

    v = 1. - v;


    fragColor = vec4(v, v, v, 1.0); 

    vec3 noise = getNoise(iFrame);
    fragColor = v > noise.z ? vec4(1) : vec4(0);
}

