const int MAX_MARCHING_STEPS = 30;
const float MIN_DIST = 6.;
const float MAX_DIST = 3.;
const float PRECISION = 0.002;
#define AA 0
#define PI 3.14159


const float K = 120.;

// iq's palette
vec3 palette( in float t)
{
    vec3 a = vec3(0.4, 0.2, 1.);
    vec3 b = vec3(0.75, 0.7, 0.7);
    vec3 c = vec3(1., 1., 1.);
    vec3 d = vec3(0.6, 0.35, 0.05);


    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 analytic_sphere(vec2 p, float r) {
    //return sphere_coord;
    float l = length(p);
    float h = r*cos(l*PI/2./(r*1.5));
    p = normalize(p/r)*asin(length(p/r)) / PI;
    //p *= smoothstep(0., 0.001, sign(p));
    return vec3(p, h*16.);
}

vec3 Ring(vec2 uv, inout vec3 col, inout vec3 col_bloom){

    float dmin=1e10, d;

    vec3 pos, posf;
    for (int i=0; i<int(get_NPARTICLES()); i++){
        vec3 tmp = texelFetch(iChannel0, ivec2(i, 0.), 0).xyz;

        pos.xy = decode(tmp.x);
        pos.z = tmp.y;

        d = 1.-smoothstep(0., .001, length(uv-pos.xy)-(1.+pos.z)/K);

        float d2 = abs(length(uv-pos.xy));

        if (d2<dmin){ 
            dmin = d2;
            posf = pos*d;
        }

        col += d*palette(float(i)/get_NPARTICLES()+iTime/8.);

        d2 = d2-(1.+pos.z)/K;
        if (iFrame >= 1) col_bloom += palette(float(i)/get_NPARTICLES()+iTime/8.)*exp(-abs(d2*d2)/.0003)*.4; 

    }
    return posf;
}

vec3 calcNormal(vec2 uv){
    vec2 e = vec2(1.0, -1.0) * 0.0001*1.45674;
    vec3 pos;
    float d;
    float dtot = 0.;
    vec3 S = vec3(0.);
    vec3 tmpS;
    for (int i=0; i<int(get_NPARTICLES()); i++){
        vec3 tmp = texelFetch(iChannel0, ivec2(i, 0.), 0).xyz;
        pos.xy = decode(tmp.x);
        pos.z = tmp.y;
        d = 1.-smoothstep(0., .1, length(uv-pos.xy)-(1.+pos.z)/K);
        tmpS =
            normalize(
                e.xyx*analytic_sphere(uv +  e.xy -pos.xy, (1.+pos.z)/K) + 
                e.yxx*analytic_sphere(uv +  e.yx -pos.xy, (1.+pos.z)/K) + 
                e.xxx*analytic_sphere(uv +  e.xx -pos.xy, (1.+pos.z)/K))*d              
                ;
        S = max(S, tmpS);

    }
    return S;
}

vec3 phong(vec3 lightDir, vec3 normal, vec3 rd, vec3 col) {
  // ambient
  float N = 2.;
  vec3 ambient = (col*(.5) *N )/N;

  // diffuse
  float dotLN = clamp(dot(lightDir, normal), 0., 1.);
  vec3 diffuse = (col * dotLN*N)/N;

  // specular
  float dotRV = clamp(dot(reflect(lightDir, normal), -rd), 0., 1.);
  vec3 specular = ((col) * pow(dotRV, 5.)*N)/N;

  return  ambient + diffuse + specular*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
  vec3 backgroundColor = vec3(0.1)*4.;

  vec3 col_ball = vec3(0.);
  vec3 col_bloom = vec3(0.);

  vec3 p = Ring(uv*1.4, col_ball, col_bloom);

  vec3 normal = calcNormal(uv*1.4);


  vec3 lightPosition = vec3(2, 2, 3);
  vec3 ld = normalize(lightPosition - p);

  vec3 rd = normalize(vec3(uv, 2)); 
  vec3 col = phong(ld, normal, rd, col_ball);

  // Output to screen
  vec3 col_tot = col_bloom+max(col*.4, vec3(0.));

  fragColor = vec4(col_tot, 1.);
}

