// found this on: https://www.vertexshaderart.com/art/aqgyqr6ASpcsZTeEu
vec2 sphere_point(float i) {
  // golden ratio
  float phi = (sqrt(5.) + 1.) / 2. - 1.;
  // golden angle
  float ga = phi * 2. * PI;

  vec2 p = vec2(0.);  
  p.x = ga * i;
  p.x /= 2. * PI;
  p.x = fract(p.x);
  p.x *= 2. * PI;

  p.y = asin(-1. + 2. * i / get_NPARTICLES());
  return p;
}
vec3 lla2xyz(vec2 latlon, float rad) {
  float rxz = rad * cos(latlon.y);

  return vec3(
    rxz * cos(latlon.x),
    rad * sin(latlon.y),
    rxz * sin(latlon.x)
  );
}


