float map(vec3 p, inout vec3 col, inout vec3 col_bloom){
    float d = 1e10;    
    p += vec3(1., 0., 1.);
    d = Ring(p, col, col_bloom);    

    return d;
}

float rayMarch(vec3 ro, vec3 rd, float start, out vec3 col, inout float min_dist, inout vec3 col_bloom) {
  float depth = start, min_d = 1e10;
  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    float d = map(p, col, col_bloom);
    depth += d;
    if (d < PRECISION){
        break;
        }
    if (d>MAX_DIST){
        break;
    }
    if (d<min_d){
        min_dist = d;
        min_d = d;
    }
    }
    return depth;
}

