vec3 init_position(vec3 seed){
    vec3 pos = vec3(seed);
    return vec3(pos);
}

vec3 init_velocity(vec3 seed){
    vec3 vel = vec3(seed.yxz);
    return vec3(vel);
}

// found this on: https://www.vertexshaderart.com/art/aqgyqr6ASpcsZTeEu
vec2 sphere_point(float i) {
  // golden ratio
  float phi = (sqrt(5.) + 1.) / 2. - 1.;
  // golden angle
  float ga = phi * 2. * PI;

  vec2 p = vec2(0.);  
  p.x = ga * i;
  p.x /= 2. * PI;
  p.x = fract(p.x);
  p.x *= 2. * PI;

  p.y = asin(-1. + 2. * i / get_NPARTICLES());
  return p;
}
vec3 lla2xyz(vec2 latlon, float rad) {
  float rxz = rad * cos(latlon.y);

  return vec3(
    rxz * cos(latlon.x),
    rad * sin(latlon.y),
    rxz * sin(latlon.x)
  );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 uv = ivec2(fragCoord.xy);


    if (uv.x>int(get_NPARTICLES())){
        fragColor = vec4(0.);
        return;
    }


    vec3 center_obj = vec3(0.);

    vec3 pos;
    vec3 vel;

    if (iFrame <= 1){
        float seedx = hash11( float(uv.x)+.1);
        float seedy = hash11( float(uv.x)+5.);
        float seedz = hash11(  float(uv.x)+1.);
        pos = init_position(vec3(seedx, seedy, seedz));
        vel = init_velocity(vec3(seedx, seedy, seedz));
        }

    else{
        vec4 state = texelFetch(iChannel0, uv, 0);

        pos.xy = decode(state.x);
        pos.z = state.y;

        vec2 tmp = sphere_point(mod(float(uv.x)-10.*floor(iTime), get_NPARTICLES()));
        vec3 id = lla2xyz(tmp, .5) + .05*(hash13(float(uv.x)+floor(iTime*2.*PI)) -.5)*(.5+.5*cos(iTime));

        vel.xy = decode(state.z);
        vel.z = state.w;

        pos += vel*.2;
        vel = (.96 * vel + .04 * -(pos-id));

        /*
        vec3 pos2;
        for (int i=-2; i<=2; i++){
            if (i == 0) continue;
            vec4 state2 = texelFetch(iChannel0, ivec2(uv.x + i, 0.), 0);

            pos2.xy = decode(state2.x);
            pos2.z = state2.y;

            vel += .01*pow((pos-pos2), vec3(1.));

        }
        */




    }

    vec4 state;

    state.x = encode(pos.xy);
    state.y = pos.z;

    state.z = encode(vel.xy);
    state.w = vel.z;


    fragColor = state;
}

