// Simple Polygon Drawing, mla, 2022
// Just a little exercise in drawing polygons, trying
// to keep everything as simple and obvious as possible.
// This one uses a fold rather than polar coordinates.

const float PI = 3.141592654;

bool exact = true;

float segment(vec2 p, vec2 q, vec2 r) {
  p -= q; r -= q;
  float k = dot(p,r)/dot(r,r);
  if (exact) k = clamp(k,0.0,1.0);
  return distance(p,k*r);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 p = 1.1*(2.0*fragCoord-iResolution.xy)/iResolution.y;
    float K = 8.0*4.0+2.0;
    float N = mod(iTime,K);
    N = min(N,K-N);
    float step = 2.0;
    float k = mod(N,step);
    k = smoothstep(1.0,2.0,k);
    N = floor(N/step) + k + 3.0;
    float s = cos(PI/N), t = sin(PI/N);
    float px = fwidth(p.x);
    p = p.yx*vec2(-1,1); // Set orientation
    vec2 n = vec2(t,-s);
    for (int i = 0; i < int(ceil(N)+1.0)/2; i++) {
      p.y = abs(p.y);
      float k = dot(p,n);
      if (k > 0.0) break;
      p -= 2.0*k*n;
    }
    float d = segment(p,vec2(0.5*s,0),vec2(s,t));
    vec3 col = vec3(0.75+0.25*cos(20.0*PI*d));
    col = mix(vec3(1,0,0),col,vec3(smoothstep(0.0,px,d-0.01)));
    col = pow(col,vec3(0.4545));
    fragColor = vec4(col,1.0);
}

