float sphere(vec3 p, vec3 c, float r) {
    return length(p-c)-r;
}
float sdBox( vec3 p, vec3 c, vec3 b )
{
  vec3 q = abs(p-c) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
vec3 light_pos() {
    return vec3(vec2(1.6, 7.), .0);
}
vec2 rot(vec2 p, float a) {
    return vec2(p.x * cos(a) - p.y * sin(a), 
                p.x * sin(a) + p.y * cos(a));
}
vec2 map_the_world(in vec3 p) {
    vec3 pbi = p;
    pbi.xy = rot(pbi.xy, sin(iTime));
    pbi.xz = rot(pbi.xz, cos(iTime));
    float s = sphere(vec3(p.x, p.y, p.z), vec3(-3., sin(iTime)+3., -3.), 1.);
    float b1 = sdBox(p, vec3(sin(2.68), cos(2.68), -3.), vec3(100., 2., 100.));
    float bi = sdBox(pbi, vec3(3., 1., 2.), vec3(1.) );
    float l = sphere(p, light_pos(), .3);
    float glass = sphere(vec3(p.x, p.y, p.z), vec3(+3., 3., -3.), .3);
    float index = 0.;
    float m = min(s, min(b1, min(bi, min(l, glass))));
    if (s == m) {
        index = 1.;
    }
    if (b1 == m) {
        index = 2.;
    }
    if (bi == m) {
        index = 3.;
    }
    if (l == m) {
        index = 4.;
    }
    if (glass == m) {
        index = 5.;
    }
    return vec2(m, index);
}
vec3 Norm(in vec3 p)
{
    const vec3 small_step = vec3(0.001, 0.0, 0.0);

    float gradient_x = map_the_world(p + small_step.xyy).x - map_the_world(p - small_step.xyy).x;
    float gradient_y = map_the_world(p + small_step.yxy).x - map_the_world(p - small_step.yxy).x;
    float gradient_z = map_the_world(p + small_step.yyx).x - map_the_world(p - small_step.yyx).x;

    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);

    return normalize(normal);
}
float shadow(vec3 ro, vec3 rd) {
    float step_size;
    vec3 pos;
    float dist;
    float d;
    for (int i = 0; i < 20;i++) {
        pos = ro + dist * rd;
        vec2 map = map_the_world(pos);
        d = map.x;
        if (d < .0001 && dist > .015) {
            if (map.y == 4.) return -10.;
            return .5;
        }
        dist += d;
    }
    return 0.;    
}
vec4 render(vec3 ro, vec3 rd) {
    float step_size;
    vec3 pos;
    float dist;
    float d;
    vec3 color = vec3(1.);
    for (int iter = 0; iter < 2; iter++) {
        for (int i = 0; i < 100;i++) {
            pos = ro + dist * rd;
            vec2 map = map_the_world(pos);
            d = map.x;
            float id = map.y;
            vec3 norm = Norm(pos);
            float light = max(.1, dot(norm, normalize(light_pos())));
            light -= shadow(pos, normalize(light_pos()));
            if (d < .001) {
                bool end = true;
                if (id == 3.) {
                    color *= vec3(.2, 1., .2);
                }
                if (id == 2.) {
                    color *= vec3(.5, .6, .4);
                }
                if (id == 1.) {
                    rd = reflect(rd, norm);ro = pos;color = vec3(1., .6, 1.);
                    end = false;
                }
                if (id == 4.) {
                    color *= vec3(10.);
                }
                if (id == 5.) {
                    rd = -norm;pos += rd;end = false;ro = pos;
                }
                if (end) {
                    return vec4(color * light, dist);
                }
            }
            dist += d;
        }
    }
    return vec4(vec3(.4, .5, .8)*color, 0.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 vuv = fragCoord/iResolution.xy;
    vuv.x *= iResolution.x / iResolution.y;
    vuv.x -= .5;
    vec2 uv = vuv * 2. - 1.;
    vec3 ro = vec3(0., 0., -6.);
    vec3 rd = normalize(vec3(uv, 1.));
    vec2 mouse = (iMouse.xy/iResolution.xy - .5) * 3.;
    rd.yz = rot(rd.yz, mouse.y);rd.xz = rot(rd.xz, mouse.x);
    ro.yz = rot(ro.yz, mouse.y);ro.xz = rot(ro.xz, mouse.x);

    // Time varying pixel color
    vec4 col = render(ro, rd);

    // Output to screen
    fragColor = col;
}

