/*
q - fire upper left thruster
a - fire lower left thruster
s - fire main (bottom) thruster
d - fire lower right thruster
e - fire upper right thruster
space - increase main thrust

Or instead of q/a/s/d/e you can use the arrow keys.
If the arrow controls feel inverted to you,
uncomment CONTROLS_INVERTED in the Common tab.

Try and hit platforms!

Pro tip: to right the rocket after a crash landing,
try jostling the rocket with small pulses from the
main thruster and then catch it before it over
corrects with the auxiliary thrusters.

Or just hit r to engage the emergency break :)
*/

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Setup
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Load rocket state
    vec4 stateBlock1 = load(iChannel0, ivec2(0, 0));
    vec4 stateBlock2 = load(iChannel0, ivec2(1, 0));

    vec2 pos = stateBlock1.xy, vel = stateBlock1.zw;
    float angle = stateBlock2.x, angVel = stateBlock2.y;
    float score = stateBlock2.z, flag = stateBlock2.w;

    // Camera zoom
    float zoom = clamp(length(pos - INITIAL_POSITION) / 0.6, 1.0, 5.0);

    // Reference frame
    vec2 refUv = uv - aspectCorrect(REF_FRAME_POS, aspect);
    if (abs(refUv.x) < 0.2 && abs(refUv.y) < 0.2) {
        uv = refUv;
    }

    // Zoom out on the scene
    else {
        uv *= zoom;
        unit *= zoom;
    }

    // Draw environment
    vec2 envUv = uv + pos;

    // Reference grid
    drawSDF(abs(mod(envUv.x, 0.25) - 0.125), vec3(0.25));
    drawSDF(abs(mod(envUv.y, 0.25) - 0.125), vec3(0.25));

    vec3 envMaterial = TERRAIN_COLOR;
    if (envUv.y > PLATFORM_OFFSET - PLATFORM_THICKNESS - 2.0 * unit) { // In the area populated with platforms
        envMaterial = PLATFORM_COLOR; // Default platform color
        float cell = round(envUv.x / PLATFORM_SPACING) * PLATFORM_SPACING;
        for (int platform=0; platform < int(score); platform++) {
            if (load(iChannel0, ivec2(platform, 1)).x == cell) { // Has been collided with
                envMaterial = PLATFORM_HIT_COLOR; // Platform color after collision
                break;
            }
        }
    }

    drawSDF(mapEnv(envUv), envMaterial);

    // Thruster animation
    vec2 rocketUv = Rotate2D(-angle) * uv;

    // Draw upper left thruster flame
    if (keypress(iChannel1, KEY_q)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[0].xy, thrusters[0].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw lower left thruster flame
    if (keypress(iChannel1, KEY_a) || keypress(iChannel1, LEFT_CONTROL)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[1].xy, thrusters[1].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw main thruster flame
    if (keypress(iChannel1, KEY_s) || keypress(iChannel1, MAIN_CONTROL)) {
        float mainFlameSize = MAIN_FLAME_SIZE;
        if (keypress(iChannel1, KEY_SPACE)) {
            mainFlameSize = MAX_FLAME_SIZE;
        }

        vec2 thrusterUv = axisAlign(rocketUv - thrusters[2].xy, thrusters[2].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, mainFlameSize)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, mainFlameSize)), vec3(1.0, 1.0, 0.0));
    }

    // Draw lower right thruster flame
    if (keypress(iChannel1, KEY_d) || keypress(iChannel1, RIGHT_CONTROL)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[3].xy, thrusters[3].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw upper right thruster flame
    if (keypress(iChannel1, KEY_e)) {
        vec2 thrusterUv = axisAlign(rocketUv - thrusters[4].xy, thrusters[4].zw);
        drawSDF(sdIsosceles2(thrusterUv, vec2(0.02, FLAME_SIZE)), vec3(1.0, 0.3, 0.0));
        drawSDF(sdIsosceles2(thrusterUv, FLAME_RATIO * vec2(0.02, FLAME_SIZE)), vec3(1.0, 1.0, 0.0));
    }

    // Draw rocket
    rocketUv = vec2(abs(rocketUv.x), rocketUv.y - 0.0243594417401);
    drawSDF(sdIsosceles(rocketUv + vec2(0.0, 0.09), vec2(0.03, 0.08)), vec3(1.0, 0.0, 0.0));
    drawSDF(sdIsosceles(Rotate2D(-0.7) * (rocketUv - vec2(0.08, -0.06)), vec2(0.03, 0.08)), vec3(0.0, 1.0, 0.0));
    drawSDF(sdIsosceles(Rotate2D(-1.8) * (rocketUv - vec2(0.07, 0.03)), vec2(0.03, 0.08)), vec3(0.0, 0.0, 1.0));
    drawSDF(sdIsosceles(rocketUv, vec2(0.08, 0.2)), vec3(1.0));

    if (abs(refUv.x) > 0.2 || abs(refUv.y) > 0.2) {
        // Reset zoom
        uv /= zoom;
        unit /= zoom;

        // Draw velocity vector
        vec2 vectorBase = normalize(vel) * 0.2;
        drawSDF(sdVectorArrow(uv - vectorBase, 0.05 * vel), vec3(1.0));

        // Show score
        color = digitIn(color, vec3(1.0), uv - aspectCorrect(SCORE_POS, aspect), 100.0, score);
    }

    // Outline the reference frame
    float frame = sdBox(refUv, vec2(0.2));
    drawSDF(abs(frame) - 0.001, vec3(1.0));

    fragColor = vec4(color, 1.0);
}

