#define load(buf, addr) texelFetch(buf, addr, 0)
#define keypress(buf, key) (texelFetch(buf, ivec2(key, 0), 0).x > 0.5)
#define aspectCorrect(p, aspect) vec2(p.x * aspect, p.y)

vec2 perp(in vec2 v) {
    return vec2(-v.y, v.x);
}

mat2 Rotate2D(in float a) {
    float co = cos(a), si = sin(a);
    return mat2(co, si, -si, co);
}

vec2 axisAlign(in vec2 p, in vec2 up) {
    return p * mat2(up.y, -up.x, up);
}

// https://www.shadertoy.com/view/4djSRW
float noise(in float p) {
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float snoise(in float p) {
    return mix(noise(floor(p)), noise(ceil(p)), smoothstep(0.0, 1.0, fract(p)));
}

float fbm(in float p) {
    float val = 0.0;
    float noiseScale = 1.0;
    float totalScale = 0.0;
    for (int oct=0; oct < TERRAIN_ROUGHNESS; oct++) {
        val += snoise(p) * noiseScale;
        totalScale += noiseScale;
        noiseScale *= 0.5;
        p *= 2.0;
    }

    return val / totalScale;
}

// SDFs
float sdVerticalLine(in vec2 p, in float h) {
    return length(vec2(p.x, p.y - h * clamp(p.y / h, 0.0, 1.0)));
}

float sdIsosceles(in vec2 p, in vec2 q) {
    p = vec2(abs(p.x), 2.0 * q.y / 3.0 - p.y);
    vec2 a = p - q * clamp(dot(p, q) / dot(q, q), 0.0, 1.0);
    vec2 b = p - q * vec2(clamp(p.x / q.x, 0.0, 1.0), 1.0);
    float s = -sign(q.y);
    vec2 d = min(vec2(dot(a, a), s * (p.x * q.y - p.y * q.x)), vec2(dot(b, b), s * (p.y - q.y)));
    return -sqrt(d.x) * sign(d.y);
}

// Uncentered isosceles triangle
float sdIsosceles2(in vec2 p, in vec2 q) {
    p = vec2(abs(p.x), q.y - p.y);
    vec2 a = p - q * clamp(dot(p, q) / dot(q, q), 0.0, 1.0);
    vec2 b = p - q * vec2(clamp(p.x / q.x, 0.0, 1.0), 1.0);
    float s = -sign(q.y);
    vec2 d = min(vec2(dot(a, a), s * (p.x * q.y - p.y * q.x)), vec2(dot(b, b), s * (p.y - q.y)));
    return -sqrt(d.x) * sign(d.y);
}

float sdBox(in vec2 p, in vec2 b) {
    p = abs(p) - b;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y));
}

float sdVectorArrow(in vec2 p, in vec2 v) {
    float mag = length(v);
    p = axisAlign(p, v / mag);
    return min(sdVerticalLine(p, mag), sdIsosceles(p - vec2(0.0, mag), vec2(0.02, 0.03)));
}

