// Controls =================================
// Mouse + Click: Change the distance checked position.

// Colors ===================================
// Black Cell:       Cell fully empty.
// Blue Cell:        Cell partially occupied (contains an edge).
// Light Blue Cell:  Cell fully occupied.
// Red-Tinted Cells: Cells checked for occupancy.
// Orange Circle:    Distance of found cell.
// Whtie Circle:     Final distance.


const int mipCount = 11;
const int maxMip = mipCount - 1;

bool containsEdge(bool mySign, ivec2 coord, int mipLevel)
{
    float occupancy = cubeFetch(iChannel0, coord, mipLevel).r;

    if(mySign)
    {
        return occupancy < 1.;
    }
    else
    {
        return occupancy > 0.;
    }
}

bool isOccupied(vec2 uv)
{
    float occupancy = cubeLod(iChannel0, uv, 0.0).r;
    return occupancy > 0.;
}


void GetSubsampleCoords(bool xNeg, bool yNeg, bool absXGtAbsY, out ivec4 c01, out ivec4 c23)
{
    int r = 0x1B; // 00 10 01 11;

    if(xNeg)
    {
        const int xor = 0xAA; // 10 10 10 10
        r = r ^ xor;
    }

    if(yNeg)
    {
        const int xor = 0x55; // 01 01 01 01
        r = r ^ xor;
    }

    if(!absXGtAbsY)
    {
        const int xor = 0x3C; // 00 11 11 00
        r = r ^ xor;
    }

    const ivec4 c01_m = ivec4
    (
        0x80, // 10 00 00 00
        0x40, // 01 00 00 00
        0x20, // 00 10 00 00
        0x10  // 00 01 00 00
    );

    const ivec4 c01_s = ivec4
    (
        7,
        6,
        5, 
        4 
    );

    const ivec4 c23_m = ivec4
    (
        0x08, // 00 00 10 00
        0x04, // 00 00 01 00
        0x02, // 00 00 00 10
        0x01  // 00 00 00 01
    );

    const ivec4 c23_s = ivec4
    (
        3, 
        2, 
        1,
        0
    );

    c01 = (ivec4(r) & c01_m) >> c01_s;
    c23 = (ivec4(r) & c23_m) >> c23_s;
}

// Returns the minimum distance from
// a given coordinate to a square whose minimum
// is coord, and whose maximum is coord+(1,1).
float getMinDistSqr(vec2 point, ivec2 coord)
{
    vec2 squareCenter = vec2(coord) + vec2(0.5);
    vec2 centerToPoint = point - squareCenter;
    vec2 minOffset = clamp(centerToPoint, vec2(-0.5), vec2(0.5));
    return lengthSqr(centerToPoint - minOffset);
}

const vec4 DebugBoundsCheckColor    = vec4(0.0, 0.0, 1.0, 0.25);
const vec4 DebugEdgeCheckColor      = vec4(1.0, 0.0, 0.0, 0.25);
const vec4 DebugStackIncrementColor = vec4(0.0, 1.0, 1.0, 0.25);
const vec4 DebugStackLimitColor     = vec4(0.0, 1.0, 0.0, 0.5);
const vec4 DebugSubDistCloserColor  = vec4(1.0, 0.5, 0.0, 1.0);
const vec4 DebugSubDistFurtherColor = vec4(0.5, 1.0, 0.0, 1.0);

const int ErrorStackUnderflow = 0;
const int ErrorStackOverflow = 1;
const int ErrorInvalidState = 2;

void drawCharacter(vec4 fontColor, in ivec2 fontElement, in vec2 fragCoord, inout vec4 fragColor)
{
    if(fontElement.x >= 16 || fontElement.y >= 16 || fontElement.x < 0 || fontElement.y < 0)
        fontElement = ivec2(0);

    vec4 text = sampleFontElementColor(iChannel2, fragCoord, fontElement, fontColor);

    blend(text, fragColor);
}

void drawError(in int errorID, in int data, in vec2 fragCoord, inout vec4 fragColor)
{
    const vec4 ErrorStackUnderflowColor = vec4(1.0, 0.2, 0.0, 1.0);
    const vec4 ErrorStackOverflowColor = vec4(1.0, 0.2, 0.2, 1.0);
    const vec4 ErrorInvalidStateColor = vec4(1.0, 0.0, 0.0, 1.0);

    ivec2 fontElement;
    vec4 fontColor;

    switch(errorID)
    {
        case ErrorStackUnderflow:
            fontElement = ivec2(1 + data, 9);
            fontColor = ErrorStackUnderflowColor;
            break;
        case ErrorStackOverflow:
            fontElement = ivec2(1 + data, 11);
            fontColor = ErrorStackOverflowColor;
            break;
        case ErrorInvalidState:
            fontElement = ivec2(1 + data, 11);
            fontColor = ErrorInvalidStateColor;
            break;
    }

    if(fontElement.x >= 16 || fontElement.y >= 16 || fontElement.x < 0 || fontElement.y < 0)
        fontElement = ivec2(0);

    vec4 text = sampleFontElementColor(iChannel2, fragCoord, fontElement, fontColor);

    blend(text, fragColor);
}

// Define the function in a way that recursion is easy to type.
