const float n_tau = 6.283185;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pix_cor_nor = (fragCoord.xy - iResolution.xy* 0.5) / iResolution.y;
    vec2 o_mou_cor_nor = (iMouse.xy - iResolution.xy* 0.5) / iResolution.y;

    if(iMouse.w == 0.0){
        o_mou_cor_nor = vec2(0.3);
    }
    float n_sides = 10.0*o_mou_cor_nor.x;
    float n_dist_origin = length(vec2(0.0) - o_pix_cor_nor);
    vec2 o_delta = vec2(0.0) - o_pix_cor_nor;
    float n_angle_origin = atan(o_delta.x , o_delta.y)+(n_tau/2.0);
    float n_angle_origin_nor = n_angle_origin / n_tau;
    float n_radius = 0.4;
    float n_dist_shortest = 1.0;

    float n_side_nor = floor(n_angle_origin_nor / (1.0/n_sides))*(1.0/n_sides);

    float n_dist_origin_max = min(n_dist_origin,n_radius);
    vec2 o_p1 = vec2(
            sin(n_tau * n_side_nor)*n_dist_origin,
            cos(n_tau * n_side_nor)*n_dist_origin
    );
    float n_dist1 = length(o_pix_cor_nor - o_p1);
    vec2 o_p2 = vec2(
            sin(n_tau * (n_side_nor+(1.0/n_sides)))*n_dist_origin,
            cos(n_tau * (n_side_nor+(1.0/n_sides)))*n_dist_origin
    );
    float n_dist2 = length(o_pix_cor_nor - o_p2);
    vec2 o_p3 = vec2(
            sin(n_angle_origin)*n_radius,
            cos(n_angle_origin)*n_radius
    );
    float n_dist3 = length(o_p3-o_pix_cor_nor);
    float n_dist = min(n_dist1, n_dist2);

    n_dist = min(n_dist, n_dist3);


    o_p1 = vec2(
            sin(n_tau * n_side_nor)*n_radius,
            cos(n_tau * n_side_nor)*n_radius
    );
    o_p2 = vec2(
            sin(n_tau * (n_side_nor+(1.0/n_sides)))*n_radius,
            cos(n_tau * (n_side_nor+(1.0/n_sides)))*n_radius
    );
    vec2 o_delta2 = o_p2-o_p1;
    float n_m = o_delta2.y / o_delta2.x;
    float n_q = -(n_m*o_p1.x) + o_p1.y;

    float n_x = o_pix_cor_nor.x;
    float n_y = n_m * n_x + n_q; 
    float n_dist4 = abs(o_pix_cor_nor.y-n_y);
    n_dist = min(n_dist, n_dist4);



    fragColor = vec4(sqrt(n_dist));


}

/*
Infinite speed motion blur using volume ray casting.

Blog post to go with it: https://www.osar.fr/notes/motionblur
*/

#define PI 3.14159265359

// basic material, light and camera settings
#define DIFFUSE .9
#define SPEC .9
#define REFLECT .05
const vec3 lightDir = normalize(vec3(-5, -6, -1));
#define CAM_D 2.4
#define CAM_H .75

// marching iterations
#define ITER 40
#define SHADOW_ITER 20
// marching step, which depends on the size of the bounding sphere
float stepSz;
// torus shape ratio = minor radius / major radius
#define TOR_RATIO .38
// speed for: time remapping; ball transition into orbit; object rotation
#define TIMESCALE .015
#define RAD_SPEED 100.
const float RT_RAD_SPEED = sqrt(RAD_SPEED);
const float MAX_SPEED = floor(30./(TIMESCALE*PI*2.)+.5)*PI*2.;
// remapped time for large scale events
float T;
// cycle duration in remapped time
// it depends on the torus ratio because the radiuses zoom into each other
const float C = log((1. + TOR_RATIO) / TOR_RATIO);
const float D = C * .5;
// ball and torus speed, rotation and transformation matrix
float balSpeed, balRot, torSpeed, torRot;
mat2 balMat, torMat;
// ball and torus size and cycle progression
float balSz, torSz, balCycle, torCycle;
// ball and torus motion blur amplification
float balAmp, torAmp;
// torus minor and major radius, with squared version
vec2 tor, tor2;
// constants for torus angle and ball normals
float torCst, balCst;
// density and normity x-fades, ball orbit radius, cosmetic adjustments
float densXf, normXf, balOrbit, torNormSz, strobe;

// by Dave_Hoskins: https://www.shadertoy.com/view/4djSRW
float hash14(vec4 p4) {
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
	p4 += dot(p4, p4.wzxy+33.33);
	return fract((p4.x + p4.y) * (p4.z + p4.w));
}

// by iq: https://iquilezles.org/articles/filterableprocedurals/
float filteredGrid(vec2 p, float scale, vec2 dpdx, vec2 dpdy) {
	float iscale = 1./scale;
	float N = 60.0*scale;
	p *= iscale;
	vec2 w = max(abs(dpdx), abs(dpdy))*iscale;
	vec2 a = p + 0.5*w;
	vec2 b = p - 0.5*w;
	vec2 i = (floor(a)+min(fract(a)*N,1.0)-
		floor(b)-min(fract(b)*N,1.0))/(N*w);
	return (1.0-i.x*.6)*(1.0-i.y*.6);
}

// by iq: https://iquilezles.org/articles/smin/
float smin(float a, float b, float k) {
	float h = max(k-abs(a-b), 0.0)/k;
	return min(a, b) - h*h*k*(1.0/4.0);
}

mat2 rot2d(float a) {
	float c = cos(a);
	float s = sin(a);
	return mat2(c, -s, s, c);
}

// 2-point sphere intersection
vec2 sphIntersect2(vec3 ro, vec3 rd, vec4 sph) {
	vec3 oc = ro - sph.xyz;
	float b = dot(oc, rd);
	float c = dot(oc, oc) - sph.w*sph.w;
	float h = b*b - c;
	if(h<0.0) return vec2(-1.0, -1.0);
	h = sqrt(h);
	return vec2(-b-h, -b+h);
}

// antiderivative of the cosine shutter function which is:
// (1-cos((x-t1) 2 PI / (t2-t1)))/(t2-t1) if t1<x<t2
// 0 otherwise
float iCosShutter(float x, float t1, float t2) {
	if (x < t1) return 0.;
	if (x > t2) return 1.;
	float d = 1./(t2 - t1);
	x -= t1;
	return x*d - sin(2.*PI*x*d)/(2.*PI);
}

// motion blurred density = integral of { object presence * window function }
float cosMotionBlur(float obj1, float obj2) {
	// Shutter time interval. Should include the frameStart, but it's
	// moved to the pixel coordinates for easier wrap management.
	float shut1 = -1./60.;
	float shut2 = 1./60.;
	// integral of the shutter function from obj1 to obj2
	return iCosShutter(obj2, shut1, shut2) - iCosShutter(obj1, shut1, shut2);
}

// Take a slice at depth y. In polar coordinates, at radius r,
// find the polar angle of the ball surface.
// Returns 0 if r is entirely outside the ball.
// Returns -1 if r is entirely inside the ball.
float ballPolarSurface(float r, float y) {
	float rad = balSz*balSz - y*y;
	if (rad <= 0.) return 0.;
	rad = sqrt(rad);
	if (r <= rad-balOrbit) return -1.;
	float div = (balOrbit*balOrbit+r*r-rad*rad)/(2.*r*balOrbit);
	if (abs(div) > 1.) return 0.;
	return acos(div);
}

// motion-blurred ball density
float ballDensity(vec3 p, float speed) {
    p.xz *= balMat;
	p.z = abs(p.z);
	vec2 pol = vec2(length(p.xz), atan(p.z, p.x));
	float bA = ballPolarSurface(pol.x, p.y);
	if (bA == -1.) return 1.;
	// Time interval for the object presence at this pixel.
	float obj1 = (pol.y-bA)/speed;
	float obj2 = (pol.y+bA)/speed;
	return cosMotionBlur(obj1, obj2);
}

// ball "normity", pseudo distance field to calculate normals
float ballNormity(vec3 p) {
    p.xz *= balMat;
	p.z = abs(p.z);
	vec2 pol = vec2(length(p.xz), atan(p.z, p.x));
	pol.y = max(0., pol.y-balCst);
	p.x = pol.x*cos(pol.y);
	p.z = pol.x*sin(pol.y);
	return length(p-vec3(balOrbit, 0., 0.))-balSz;
}

// Take a slice at depth z. In polar coordinates, at radius r,
// find the polar angle of the torus surface.
// Returns 0 if r is entirely outside the torus.
// Returns -1 if r is entirely inside the torus.
float spiricPolarSurface(float r, float z) {
	float r2 = r*r;
	float z2 = z*z;
	float sum = torCst-2.*tor2.x*z2-2.*tor2.x*r2-2.*tor2.y*z2+2.*tor2.y*r2+z2*z2+2.*z2*r2+r2*r2;
	if (sum < 0.) return -1.;
	float sq = sqrt(sum)/(2.*tor.y*r);
	if (abs(sq) > 1.) return 0.;
	return acos(sq);
}

// motion-blurred density of a half torus (a macaroni)
float halfTorusDensity(vec2 pol, float z, float speed) {
	float da = spiricPolarSurface(pol.x, z);
	if (da == 0.) return 0.;
	if (da == -1.) return 1.;
	// Time interval for the object presence at this pixel.
	float obj1 = (pol.y-da)/speed;
	float obj2 = (pol.y+da)/speed;
	return cosMotionBlur(obj1, obj2);
}

// motion-blurred torus density
float torusDensity(vec3 p3d, float speed) {
    p3d.xy *= torMat;
	vec2 pol = vec2(length(p3d.xy), atan(p3d.y, p3d.x));
	pol.y = mod(pol.y, PI*2.)-PI;
	float da = halfTorusDensity(pol, p3d.z, speed);
	pol.y = mod(pol.y + PI*2., PI*2.)-PI;
	float da2 = halfTorusDensity(pol, p3d.z, speed);
	if (da == 0. && da2 == 0.) return 0.;
	if (da == -1. || da2 == -1.) return 1.;
	return min(1., da+da2);
}

// torus "normity", pseudo distance field to calculate normals
float torusNormity(vec3 p, float speed) {
    p.xy *= torMat;
	float shell = abs(length(p)-tor.y)-tor.x*.3;
	vec2 q = vec2(length(p.xz)-tor.y,p.y);
	float torus = length(q)-tor.x;
	return -smin(speed*.002-torus, .1-shell, 0.1);
}

// combined density and normity
float density(vec3 p) {
	float ball = ballDensity(p, balSpeed)*balAmp;
	float torus = torusDensity(p, torSpeed)*torAmp;
    return mix(ball, torus, densXf);
}
float normity(vec3 p) {
    return mix(
		ballNormity(p),
    	torusNormity(p*torNormSz, torSpeed*.5),
		normXf);
}
vec3 getNormal(vec3 p) {
	float d = normity(p);
	vec2 e = vec2(.001, 0);
	vec3 n = d - vec3(
		normity(p-e.xyy),
		normity(p-e.yxy),
		normity(p-e.yyx));
	return normalize(n);
}

// Because we're raycasting translucent stuff, this is called up to 28x per px
// so let's keep it short
vec3 material(vec3 normal, vec3 rayDir) {
	float diff = max(dot(normal, -lightDir), .05);
	vec3 reflectDir = -lightDir - 2.*normal * dot(-lightDir, normal);
	float spec = max(dot(rayDir, reflectDir), 0.);
	return vec3(.8,.9,1.) * (diff * DIFFUSE + spec * REFLECT);
}

// render torusphere by volume raycasting
vec4 march(vec3 ro, vec3 rd, float marchPos, float marchBack) {
	float totMul = strobe*stepSz/0.05;
	vec4 col = vec4(0.);
	marchPos -= stepSz * hash14(vec4(rd*4000., iTime*100.));
	int nMats = 0;
	for(int i=0; i<ITER; i++) {
		vec3 pos = ro + rd * marchPos;
		float d = clamp(density(pos)*totMul, 0., 1.);
		if(d > .002) {
			d = d*d*.5;
			float a2 = (1.-col.a)*d;
			vec3 n = getNormal(pos);
			col += vec4(material(n, rd)*a2, a2);
			if (col.a > 0.95) break;
			if (nMats++ > 28) break;
		}
		marchPos += stepSz;
		if (marchPos > marchBack) break;
	}
	if (col.a > 0.) col.rgb /= col.a;
	return col;
}

// render ground shadow by volume raycasting without material
float shadowMarch(vec3 ro, vec3 rd, float marchPos, float marchBack) {
	float ret = 0.;
	float shadowStep = stepSz*2.;
	float totMul = .47*strobe*shadowStep/0.05;
	marchPos -= shadowStep * hash14(vec4(ro*4000., iTime*100.));
	for(int i=0; i<SHADOW_ITER; i++) {
		vec3 pos = ro + rd * marchPos;
		float d = clamp(density(pos)*totMul, 0., 1.);
		if(d > .002) {
			d = d*d*.9;
			ret += (1.-ret)*d;
			if (ret > 0.95) break;
		}
		marchPos += shadowStep;
		if (marchPos > marchBack) break;
	}
	return min(1., ret);
}

// very inefficiently speed up the boring parts
float retime(float t) {
	t *= TIMESCALE;
	float s = .5+1.7*t*PI*2./D;
	s = sin(s+sin(s+sin(s+sin(s)*0.3)*0.5)*0.75);
	return s*.06+t*1.7;
}

// ball<->torus crossfade used separately by density and normity
float getXf(float x) {
	x = (abs(mod(x-(D/4.), C)-D)/D-.5)*2.+.5;
	// return smoothstep(0, 1, x)
	x = 2.*clamp(x, 0., 1.)-1.;
	return .5+x/(x*x+1.);
}

// The entire scene is necessarily zooming out. The ground texture deals with
// that by crossfading different scales.
const float GRID_CYCLE = log(64.);
vec3 grid(vec2 pt, vec2 dx, vec2 dy, float phase, float t) {
	float freq = exp(-mod(t+GRID_CYCLE*phase, GRID_CYCLE))*7.;
	float amp = cos(PI*2.*phase+t*PI*2./GRID_CYCLE)*-.5+.5;
	float g = filteredGrid(pt, freq, dx, dy)*amp;
	return vec3(g,g,g);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	// set all the globals...
	T = retime(iTime+25.); // consider a modulo here
	balCycle = mod(T, C);
	torCycle = mod(T+D, C);

	// size of the bounding sphere for marching and step size
	float boundSz = exp(-min(torCycle, 5.*(C-mod(T-D, C))));
	stepSz = boundSz/20.;

	// the ball/torus appear constant size and the camera appears to zoom out
	// in the code the camera distance is fixed and the objects are shrinking
	balSz = exp(-balCycle-D);
	torSz = exp(-torCycle);

	// the rotation is (theoretically) the integral of the speed, we need both
	balSpeed = .04*MAX_SPEED*(cos(T*PI*2./C)+1.);
	torSpeed = .04*MAX_SPEED*(cos((T+D)*PI*2./C)+1.);
	balRot = MAX_SPEED*(sin(T*PI*2./C)/(PI*2./C)+T)/C;
	torRot = MAX_SPEED*(sin((T+D)*PI*2./C)/(PI*2./C)+T)/C;
	if (balCycle<D) {
		balRot = MAX_SPEED*(floor(T/C+.5)*C+D)/C;
		balSpeed = 0.;
	}
	if (torCycle<D) {
		torRot = MAX_SPEED*(floor((T+D)/C+.5)*C)/C;
		torSpeed = 0.;
	}
	balMat = rot2d(balRot);
	torMat = rot2d(torRot);

	// torus minor and major radius and their squares
	tor = vec2(torSz/(1.+1./TOR_RATIO), torSz/(1.+TOR_RATIO));
	tor2 = tor*tor;

	// precalculate constants for the spiric angle and ball normals
	torCst = tor2.x*tor2.x + tor2.y*tor2.y - 2.*tor2.x*tor2.y;
	balCst = 2.*balSpeed*smoothstep(30., 40., balSpeed);
	float bx = balCst*.037;
	balCst = (balCst+bx*bx*bx)*.004;

	// ball's orbital radius
	balOrbit = clamp(balCycle-D, 0., 2.*RT_RAD_SPEED/RAD_SPEED)-RT_RAD_SPEED/RAD_SPEED;
	balOrbit = .5+RT_RAD_SPEED*balOrbit/(balOrbit*balOrbit*RAD_SPEED+1.);
	balOrbit *= tor.y;

	// ball<->torus crossfade: the normity precedes the density slightly
	// this smoothens the max speed -> zero speed illusion
	densXf = getXf(T);
	normXf = getXf(T+0.06);

	// motion blur amplification is what makes this work
	balAmp = 1.+balSpeed*balSpeed*.00013;
	torAmp = 1.5+torSpeed*torSpeed*.00015;
	torNormSz = max(1., 8.*(torCycle-.76));

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	// the strobe effect simulates overlap between fast spin and slow spin
	strobe = 1.-.1*(sin(iTime*83.+PI*smoothstep(.4, .6, uv.x))+1.)*(sin(2.2+T*PI*2./D)+1.)*.5;

	// camera
	float side = cos(CAM_H)*CAM_D;
	float camT = iTime*0.05+PI*.75;
	vec3 ro = vec3(sin(camT)*side, sin(CAM_H)*CAM_D, cos(camT)*side); // camera position (ray origin)
	vec3 ta = vec3(0., 0., 0.); // camera target
	vec3 ww = normalize(ta - ro);
	vec3 uu = normalize(cross(ww,vec3(0.0,1.0,0.0)));
	vec3 vv = normalize(cross(uu,ww));
	vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
	vec3 rd = normalize(p.x*uu + p.y*vv + 2.*ww);

	// this starting color taints the entire scene (unintentional but why not)
	vec3 col = vec3(0.33, 0.18, 0.1);

	// the ground plane
	if (rd.y < 0.) {
		vec3 groundPt = ro + rd*(-(ro.y+.8) / rd.y);
		vec2 g2d = groundPt.xz;
		vec2 dx = dFdx(g2d);
		vec2 dy = dFdy(g2d);
		// the ground texture zooms out by crossfading different scales
		col += grid(g2d, dx, dy, 0., T)/3.;
		col += grid(g2d, dx, dy, 1./3., T)/3.;
		col += grid(g2d, dx, dy, 2./3., T)/3.;
		float sqDist = dot(g2d, g2d);
		col *= 2./(sqDist*.5*1.5+1.)-1.2/(sqDist*1.5*1.5+1.);
		// are we in the shadow of the bounding sphere?
		vec2 sphInter = sphIntersect2(groundPt, -lightDir, vec4(0.,0.,0.,boundSz));
		if (sphInter != vec2(-1., -1.)) {
			// march the torusphere to draw the shadow
			float shad = shadowMarch(groundPt, -lightDir, sphInter.x, sphInter.y);
			col *= 1.-shad*.7;
		}
	}

	// the sky (only visible in interactive version)
	float up = dot(rd, vec3(0.,1.,0.));
	col = mix(col, vec3(0.33, 0.18, 0.1)*.7, 1.-smoothstep(0., .02, abs(up)+.003));
	col = mix(col, vec3(0.,0.,.1), smoothstep(0., .5, up));

	// finally render the torusphere
	vec2 sphInter = sphIntersect2(ro, rd, vec4(0.,0.,0.,boundSz));
	if (sphInter != vec2(-1., -1.)) {
		vec4 ts = march(ro, rd, sphInter.x, sphInter.y);
		col = mix(col, ts.rgb, ts.a);
	}
    fragColor = vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

       vec2 o_pix_coo_nor = (fragCoord.xy-(iResolution.xy/2.0)) / iResolution.y; 
       vec2 o_mou_coo_nor = (iMouse.xy-(iResolution.xy/2.0)) / iResolution.y; 



       float[] a_n_value =  float[](
        300.0,
        200.0,
        150.0,
        400.0,
        200.0,
        350.0,
        150.0,
        250.0,
        50.0
        );

        float n_val_sum = 0.0;
        for(float n_i = 0.0; n_i < float(a_n_value.length()); n_i+=1.){
            n_val_sum+=a_n_value[int(n_i)];
        }

        float n_tau = 6.283185;
        float n_val_nor_start = 0.0;
        float n_val_nor_end = 0.0;
        vec2 o_delta = vec2(0.0,0.0)-o_pix_coo_nor;
        for(float n_i = 0.0; n_i < float(a_n_value.length()); n_i+=1.){
            float n_val_nor = a_n_value[int(n_i)] / n_val_sum;
            n_val_nor_end += n_val_nor; 
            float n_tau = 6.2831;
            float n_angle = atan(o_delta.y , o_delta.x)+(n_tau/2.0);
            float n_angle_norm = n_angle / n_tau;

            if(
                n_angle_norm > n_val_nor_start
                &&
                n_angle_norm < n_val_nor_end
            ){

                float n_radius = length(vec2(0.0,0.0)-o_pix_coo_nor);
                float n_radius_max = 0.5;

                vec2 o_p1 = vec2(
                    cos((n_val_nor_start)*n_tau)*n_radius, 
                    sin((n_val_nor_start)*n_tau)*n_radius
                );
                float n_dist1 = length(o_p1 - o_pix_coo_nor);

                vec2 o_p2 = vec2(
                    cos((n_val_nor_end)*n_tau)*n_radius, 
                    sin((n_val_nor_end)*n_tau)*n_radius
                );
                float n_dist2 = length(o_p2 - o_pix_coo_nor);

                vec2 o_p3 = vec2(
                    cos((n_angle_norm)*n_tau)*n_radius_max, 
                    sin((n_angle_norm)*n_tau)*n_radius_max
                );
                float n_dist3 = length(o_p3 - o_pix_coo_nor);
                float n_dist_min = min(n_dist1, n_dist2);
                n_dist_min = min(n_dist_min, n_dist3);

                fragColor = vec4((mod(n_dist_min+iTime*0.1,0.1)/0.1));
            }
            n_val_nor_start = n_val_nor_end;

        }


}

// Dihedral Symmetry, mla, 2022
// Started as a little exercise in drawing polygons, trying to
// keep everything as simple as possible, but I couldn't resist
// adding some bells & whistles.

const float PI = 3.141592654;

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

// Signed distance from p segment qr. Positive distance for
// points on the left of the line looking from q to r.
float ssegment(vec2 p, vec2 q, vec2 r) {
  p -= q; r -= q;
  float k = dot(p,r)/dot(r,r);
  k = clamp(k,0.0,1.0);
  vec2 closestpoint = k*r;
  float dist = distance(p,closestpoint);
  vec2 normal = vec2(-r.y,r.x);
  float s = dot(p,normal) >= 0.0 ? 1.0 : -1.0;
  return s*dist;
}


float polydist(float N, vec2 p, inout float t) {
    float phi = PI/N; // angle of half sector
    float theta = atan(p.x,-p.y);// negative y-axis is theta = 0
    theta = mod(theta+phi,2.0*phi)-phi;
    //theta /= 2.0*phi; theta -= round(theta); theta *= 2.0*phi; // Equivalent
    theta = abs(theta); // Mirror symmetry
    // With dihedral symmetry, the closest point is always in the
    // fundamental region, so can limit attention just to there
    // for finding the SDF.
    vec2 p1 = length(p)*vec2(cos(theta),sin(theta));
    float k = 0.618; // Just a constant for drawing
    //k = abs(tan(0.125*PI*iTime)); k *= k; t = 1.0; // Alternative animation
    vec2 q0 = 1.0/k*vec2(cos(phi),sin(phi)), q1 = k*vec2(cos(phi),0);
    return ssegment(p1,q0,mix(q0,q1,t));
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 p = 2.0*(2.0*fragCoord-iResolution.xy)/iResolution.y;
    //float N = iMouse.x <= 0.0 ? 7.0 : floor(10.0*iMouse.x/iResolution.x) + 3.0;
    //float N = floor(mod(0.25*iTime,10.0)) + 3.0;
    float t = fract(0.25*iTime);
    t = 6.0*t;
    float N = 0.25*iTime;
    float N1 = N+1.0;
    N = floor(mod(N,10.0))+3.0;
    N1 = floor(mod(N1,10.0))+3.0;
    if (t > 5.0) N = mix(N,N1,smoothstep(0.0,1.0,t-5.0));
    t = min(t,6.0-t);
    t = clamp(t-1.0,0.0,1.0);
    t = smoothstep(0.0,1.0,t);
    float d = polydist(N,p,t);
    float px = fwidth(p.x);
    vec3 col = vec3(0.75+0.25*cos(20.0*PI*abs(d)));
    if (t == 1.0 && d < 0.0) col.b = 0.25;
    col = mix(vec3(1,0,0),col,vec3(smoothstep(0.0,px,abs(d)-0.01)));
    col = pow(col,vec3(0.4545));
    if (alert) col.r = 1.0;
    fragColor = vec4(col,1.0);
}

// CCO: Mandala flowers
//  Smoooth kaleidoscope + abstract shape + colors

#define RESOLUTION  iResolution
#define TIME        iTime
#define PI          3.141592654
#define PI_2        (0.5*3.141592654)
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

#define NO_HEXAGON_LINES

// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 p) {
  float a = dot (p, vec2 (127.1, 311.7));
  return fract (sin(a)*43758.5453123);
}

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const float logo_radius= 0.25;
const float logo_off   = 0.25;
const float logo_dx    = 0.5/sqrt(3.0);
const float logo_width = 0.1;

float rcp(float x) {
  return 1.0 / x;
}

//P. Gilcher '21, strange approximation
// Source found at: https://www.shadertoy.com/view/flSXRV
float fast_atan2(float y, float x) {
  float cosatan2 = x * rcp(abs(x) + abs(y));
  float t = PI_2 - cosatan2 * PI_2;
  return y < 0.0 ? -t : t;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions2d
float hex(vec2 p, float r) {
#ifndef NO_HEXAGON_LINES
  const vec3 k = vec3(-sqrt(3.0)*0.5,0.5,sqrt(1.0/3.0));
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
#else
  return 1.0;
#endif
}

// License: Unknown, author: Martijn Steinrucken, found: https://www.youtube.com/watch?v=VmrIDyYiJBA
vec2 hextile(inout vec2 p) {
  // See Art of Code: Hexagonal Tiling Explained!
  // https://www.youtube.com/watch?v=VmrIDyYiJBA
  const vec2 sz       = vec2(1.0, sqrt(3.0));
  const vec2 hsz      = 0.5*sz;

  vec2 p1 = mod(p, sz)-hsz;
  vec2 p2 = mod(p - hsz, sz)-hsz;
  vec2 p3 = dot(p1, p1) < dot(p2, p2) ? p1 : p2;
  vec2 n = ((p3 - p + hsz)/sz);
  p = p3;

  n -= vec2(0.5);
  // Rounding to make hextile 0,0 well behaved
  return round(n*2.0)*0.5;
}

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float modPolar(inout vec2 p, float repetitions) {
  float angle = 2.0*PI/repetitions;
  float a = atan(p.y, p.x) + angle/2.;
  float r = length(p);
  float c = floor(a/angle);
  a = mod(a,angle) - angle/2.;
  p = vec2(cos(a), sin(a))*r;
  // For an odd number of repetitions, fix cell index of the cell in -x direction
  // (cell index would be e.g. -5 and 5 in the two halves of the cell):
  if (abs(c) >= (repetitions/2.0)) c = abs(c);
  return c;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/index.htm
vec3 postProcess(vec3 col, vec2 q) {
  //  Found this somewhere on the interwebs
  col = clamp(col, 0.0, 1.0);
  // Gamma correction
  col = pow(col, 1.0/vec3(2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  // Vignetting
  col*= 0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/smin
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float pabs(float a, float k) {
  return pmax(a, -a, k);
}

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float modMirror1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize,size) - halfsize;
  p *= mod(c, 2.0)*2.0 - 1.0;
  return c;
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

// License: CC0, author: Mrten Rnge, found: https://github.com/mrange/glsl-snippets
float smoothKaleidoscope(inout vec2 p, float sm, float rep) {
  vec2 hp = p;

  vec2 hpp = toPolar(hp);
  float rn = modMirror1(hpp.y, TAU/rep);

  float sa = PI/rep - pabs(PI/rep - abs(hpp.y), sm);
  hpp.y = sign(hpp.y)*(sa);

  hp = toRect(hpp);

  p = hp;

  return rn;
}

float stripes(float d) {
  const float cc = 0.42;
  d = abs(d)-logo_width*cc;
  d = abs(d)-logo_width*cc*0.5;
  return d;
}

vec4 merge(vec4 s0, vec4 s1) {
  bool dt = s0.z < s1.z; 
  vec4 b = dt ? s0 : s1;
  vec4 t = dt ? s1 : s0;

  b.x *= 1.0-exp(-max(80.0*(t.w), 0.0));

  vec4 r = vec4(
      mix(b.xy, t.xy, t.y)
    , b.w < t.w ? b.z : t.z 
    , min(b.w, t.w)
    );

  return r;
}

vec4 figure_8(float aa, vec2 p) {
  vec2  p1 = p-vec2(logo_dx, -logo_off);
  float d1 = abs(circle(p1, logo_radius));
  float a1 = fast_atan2(-p1.x, -p1.y);
  float s1 = stripes(d1);
  float o1 = d1 - logo_width;

  vec2  p2 = p-vec2(logo_dx, logo_off);
  float d2 = abs(circle(p2, logo_radius));
  float a2 = fast_atan2(p2.x, p2.y);  
  float s2 = stripes(d2);
  float o2 = d2 - logo_width;

  vec4 c0 = vec4(smoothstep(aa, -aa, s1), smoothstep(aa, -aa, o1), a1, o1);
  vec4 c1 = vec4(smoothstep(aa, -aa, s2), smoothstep(aa, -aa, o2), a2, o2);

  return merge(c0, c1);
}

vec4 figure_half_8(float aa, vec2 p) {
  vec2  p1 = p-vec2(logo_dx, -logo_off);
  float d1 = abs(circle(p1, logo_radius));
  float a1 = fast_atan2(-p1.x, -p1.y);
  float s1 = stripes(d1);
  float o1 = d1 - logo_width;

  vec4 c0 = vec4(smoothstep(aa, -aa, s1), smoothstep(aa, -aa, o1), a1, o1);

  return c0;
}

vec2 flipy(vec2 p) {
  return vec2(p.x, -p.y);
}

vec4 clogo(vec2 p, float z, out float d) {
  float iz = 1.0/z;
  p *= iz;
  float aa = iz*2.0/RESOLUTION.y;
  float  n = modPolar(p, 3.0);

  vec4 s0 = figure_8(aa, p);
  vec4 s1 = figure_half_8(aa, p*ROT(2.0*PI/3.0));
  vec4 s2 = figure_half_8(aa, flipy(p*ROT(4.0*PI/3.0)));
  s1.z += -PI;

  vec4 s = s0;
  s = merge(s, s1);
  s = merge(s, s2);

  d = s.w;
  vec3 hsv = vec3(fract(s.z/PI+TIME*0.5), 0.9, 1.0);
  return vec4(hsv2rgb(hsv)*s.x, s.y);
}

vec3 effect(vec2 p, vec2 q) {
  float aa = 2.0/RESOLUTION.y;

  float d;
  float a = TAU*TIME*((iMouse.x!=0. && iMouse.z>=0.) ? iMouse.x/iResolution.x/10.0 : 1.0)/300.0;
  float aY = TAU*TIME*((iMouse.y!=0. && iMouse.z>=0.) ? iMouse.y/iResolution.x/10.0 : 1.0)/300.0;
  p.x += 10.0*vec2(sin(a), sin(sqrt(0.5)*a)).x;
  p.y += 10.0*vec2(sin(aY), sin(sqrt(0.5)*aY)).y;
  vec2 hp = p;
  vec2 np = hextile(hp);
  float hd = hex(hp.yx, 0.5);
  hd = abs(hd) - 2.0*aa;
  vec2 cp = hp;
  float h = hash(np);
  float hh = fract(137.0*h);
  float sm = mix(mix(0.025, 0.25, hh), 0.025, h);
  float rep = 2.0*floor(mix(8.0, 30.0, h));
  float cn = smoothKaleidoscope(cp, sm, rep);
  cp *= ROT(TIME*((iMouse.x!=0. && iMouse.z>=0.) ? max(iMouse.x,iMouse.y)/iResolution.x/50.0 : 1.0)*0.2+TAU*h);

  vec4 ccol = clogo(cp, 0.6, d);
  vec3 gcol = hsv2rgb(vec3(h, 0.8, 4.0));
  vec3 col  = vec3(0.0);
  col += gcol*exp(-50.0*max(d, 0.0));
  col = mix(col, vec3(0.2), smoothstep(aa, -aa, hd));
  col = mix(col, ccol.xyz, ccol.w);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = effect(p, q);
  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0);

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float a = length(uv - 0.25 * vec2(cos(iTime), sin(iTime))) - 0.1 * (cos(10.0 * uv.x) + sin(10.0 * uv.y)) - 0.25;
    float b = length(uv - 0.25 * vec2(2.0 * cos(3.0 * iTime), 0.5 * sin(iTime))) - 0.25;
    if (a < 0.0) fragColor = vec4(0.0, 1.0, 0.0, 1.0);
    if (b < 0.0) fragColor = vec4(0.0, 0.0, 1.0, 1.0);

    uv = fragCoord / iResolution.xy;
    fragColor += texture(iChannel1, uv);

    vec4 bufA = texture(iChannel0, uv, 100.0);
    vec2 pos = bufA.xy / bufA.w;
    if (length(uv - pos) < 0.01) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
}

